css:
@-moz-document url("http://loytonia.forumactif.org/"), url("http://loytonia.forumactif.org/faq"), url("http://loytonia.forumactif.org/memberlist"), url("http://loytonia.forumactif.org/groups"), url-prefix("http://loytonia.forumactif.org/profile"), url-prefix("http://loytonia.forumactif.org/search"), url-prefix("http://loytonia.forumactif.org/privmsg"), regexp("http://loytonia.forumactif.org/f[0-9]+.*$"), regexp("http://loytonia.forumactif.org/t[0-9]+.*$"), regexp("http://loytonia.forumactif.org/u[0-9]+.*$"), regexp("http://loytonia.forumactif.org/chatbox.*$"), regexp("http://loytonia.forumactif.org/g[0-9]+.*$"), regexp("http://loytonia.forumactif.org/abuse.*$"), regexp("http://loytonia.forumactif.org/post.*$"), regexp("http://loytonia.forumactif.org/modcp.*$") {
html#min-width,
.forumline,
body.chatbox,
body,
#sceditor_smilies,
#sce_smilies_body {
	background: #1B1B1B
}
.three-col,
.bodyline,
td.row1,
td.row2,
td.row3,
td.row1+td,
td.row1.over:hover,
td.row2over:hover,
td.row3.over:hover,
td.row3Right,
td.spaceRow,
.post-options,
.member-title,
.chatbox_row_1,
#profile-advanced-details .message-header {
	background: #252525 !important;
}
.bodyline>table>tbody>tr>td,
.chatbox_row_2,
#chatbox_header,
.postbody .quote,
.postbody .cont_code,
.postbody .spoiler,
.postbody .resizebox {
	background: #3C3C3C;
}
.bodyline>table>tbody>tr>td>a:hover,
.bodyline>table>tbody>tr>td>a:focus {
	background: #292929
}
hr,
#chatbox > p,
#chatbox_bottom,
.postbody .quote,
.postbody .cont_code,
.postbody .spoiler,
.postbody .resizebox {
	border-color: #3C3C3C
}
#text_editor_iframe,
input.post,
select,
textarea.inputbox,
textarea.post,
.sceditor-container,
.sceditor-container iframe,
.sceditor-container textarea {
	background: #4E4E4E !important;
	color: #d8d8d8 !important;
}
.sceditor-toolbar {
	background: #3A3A3A;
}
.postbody,
.gen,
.genmed,
.postdetails,
div.topictitle,
h1,
h2,
.bodyline>table>tbody>tr>td>a,
.label > span,
[class^="chatbox_row_"] .msg span,
form[action="/profile"] .gen > span > *,
#profile-advanced-details dl > dt > span,
.date-and-time,
ul.abuse label {
	color: #D8D8D8 !important
}
.nav,
.gensmall {
	color: #D8D8D8;
}
.spoiler_content,
.resizebox .resize_border,
.code,
.postbody .quote,
.postbody .cont_code,
.postbody .spoiler,
.postbody .resizebox {
	border: 0;
	color: #D8D8D8 !important;
	background: transparent
}
.spoiler,
.codebox,
.resizebox {
	background: rgba(255, 255, 255, .04) !important;
}
/* Les boutons */

input[type="submit"].mainoption,
input[type="submit"].button,
input[type="reset"].mainoption,
input[type="reset"].button,
input[type=button].mainoption,
input[type=button].button,
td.nav+td[align="left"] a[href$="mode=newtopic"],
span.nav a[href$="mode=newtopic"],
form[action="/viewforum"] a[href$="mode=newtopic"],
.post-options a[href$="mode=quote"],
.post-options a[href$="mode=editpost"],
.post-options a[href$="mode=delete"] {
	background: #1F617B !important;
	color: #C9D9E0 !important;
}
.post-options a[href$="mode=quote"]:hover,
.post-options a[href$="mode=quote"]:focus,
.post-options a[href$="mode=editpost"]:hover,
.post-options a[href$="mode=editpost"]:focus,
.post-options a[href$="mode=delete"]:hover,
.post-options a[href$="mode=delete"]:focus,
td.nav+td[align="left"] a[href$="mode=newtopic"]:hover,
td.nav+td[align="left"] a[href$="mode=newtopic"]:focus,
span.nav a[href$="mode=newtopic"]:hover,
span.nav a[href$="mode=newtopic"]:focus,
form[action="/viewforum"] a[href$="mode=newtopic"]:hover,
form[action="/viewforum"] a[href$="mode=newtopic"]:focus,
input[type="submit"].mainoption:hover,
input[type="submit"].mainoption:focus,
input[type="submit"].button:hover,
input[type="submit"].button:focus,
input[type="reset"].mainoption:hover,
input[type="reset"].mainoption:focus,
input[type="reset"].button:hover,
input[type="reset"].button:focus,
input[type=button].mainoption:hover,
input[type=button].mainoption:focus,
input[type=button].button:hover,
input[type=button].button:focus {
	background: #267898 !important;
}
input[type="submit"],
input[type="reset"],
input[type=button] {
	background: #A5A5A5 !important;
}
td.nav+td[align="left"] td.nav+td[align="left"] a[href$="mode=reply"],
span.nav a[href$="mode=reply"],
form[action="/viewforum"] a[href$="mode=reply"],
input[type="submit"],
input[type="reset"],
input[type=button] {
	background: #808080 !important;
}
td.nav+td[align="left"] td.nav+td[align="left"] a[href$="mode=reply"]:hover,
span.nav a[href$="mode=reply"]:hover,
form[action="/viewforum"] a[href$="mode=reply"]:hover,
input[type="submit"]:hover,
input[type="submit"]:focus,
input[type="reset"]:hover,
input[type="reset"]:focus,
input[type=button]:hover,
input[type=button]:focus {
	background: #A7A6A6 !important;
}
.post-options a[href$="mode=delete"],
a.kocal.kocal-to-hide {
	background: #7B1F1F !important;
}
.post-options a[href$="mode=delete"]:hover,
.post-options a[href$="mode=delete"]:focus,
a.kocal.kocal-to-hide:hover,
a.kocal.kocal-to-hide:focus {
	background: #A02929 !important;
}
a.kocal.kocal-to-show {
	background: #2D6D10
}
a.kocal.kocal-to-show:hover,
a.kocal.kocal-to-show.focus {
	background: #388416
}
.blur {
	color: #4a4a4a;
}
.fade {
	background: transparent;
}
#profile-advanced-details .message-header {
	border-top-color: #171717;
}
}
