css:
@-moz-document domain("encyclopediadramatica.se") {
/* 
by beepee, 10 Aug 2015 
sorry if it breaks your ish
built while using adblock and ghostery on chrome
*/

body {
	background: #222;
	color: white;
}

a {
	color: #aaccff
}

a:visited {
	color: #aa3399
}

table[style*="background"][style*="#fff"], table[style*="background"][style*="white"] {
	background-color: #222 !important;
	color: white;
}

table#toc {
	background-color: #222 !important;
	color: white;
}

td[bgcolor] {
	background-color: #222 !important;
	color: white;
}

div[style*="background"][style*="#fff"] {
	background-color: #222 !important;
	color: white;
}
div[style*="background"][style*="white"] {
	background-color: #222 !important;
	color: white;
}

h1, h2, h3, h4, h5 {
	background-color: #222 !important;
	color: white;
}

div.pBody, div.thumbinner {
	background-color: #222 !important;
	color: white;
}

div#content, div#catlinks, div#footer {
	background: #222;
	color: white;
	border-color: #DDD;
}

div.notice.metadata {
	background-color: #222 !important;
	color: yellow;
}

table.gallery td {
	background-color: #222;
	color: white;
}

table.toccolours td,th {
	background-color:#222;
	color: #888;
}

table.toccolours {
	background-color:#222;
	color: #888;
}

#p-cactions li a {
	background-color:#222;
	color: #888;
}

#p-cactions li.selected a {
	background-color:#222;
	color: #FFF;
}

#searchInput {
	background-color:#222;
	color: #888;
}

input.searchButton {
	background-color:#444;
	color: #CCC;
}

}
