/***  Use with light themes, e.g, kae=-1  ***/
/***  DUCK_ON_BLACK  ***************************************************************************/
@-moz-document url(https://duckduckgo.com/assets/logo_homepage.normal.v107.svg),
               url(https://duckduckgo.com/assets/logo_homepage_small.normal.v107.svg),
               url(https://duckduckgo.com/assets/logo_homepage_mobile.normal.v107.svg){
  svg > g > circle:first-child{
    fill: #000000;
  }
  svg > g > circle:not(:first-child),
  svg > g > ellipse{
    stroke: #000000;
  }
}
@-moz-document url(https://duckduckgo.com/assets/logo_header.v107.lg.svg),
               url(https://duckduckgo.com/assets/logo_header.v107.min.svg){
  svg > circle:first-child{
    fill: #000000;
  }
}
/***********************************************************************************************/

@-moz-document domain("duckduckgo.com") {

/*** DUCK_ON_BLACK *******************************************************************************************************************************/
  .no-touch .zcm__link:hover:active,
  .no-touch .zcm__link:focus:active,
  .no-touch .zcm__link:hover,
  .no-touch .zcm__link:focus,
  .no-touch .header-wrap .zcm__link.is-active {
    color: #000000;
  }
  .site-wrapper-border {
    background: #333;
  }
  .header-wrap--home,
  #header_wrapper {
    border-top-color: #333;
  }
  .search:hover .search__button,
  .search__input:focus ~ .search__button,
  .search--header.has-text.search--hover .search__button,
  .search--header.has-text.search--focus .search__button,
  .search--home.has-text .search__button {
    background-color: #181818;
    color: #646464;
  }
  .search__button:hover,
  .search__button:focus,
  .search:hover .search__button:focus,
  .search--header.has-text.search--hover .search__button:hover,
  .search--header.has-text.search--focus .search__button:hover,
  .search--home.has-text .search__button:focus,
  .search--home.has-text .search__button:hover {
    background-color: #252525;
    color: #646464;
  }
  .tile-nav.can-scroll, 
  .tile-nav.can-scroll:hover, .tile-nav.can-scroll:hover::after {
    background-color: #000000 !important;
  }
  .tile-nav.can-scroll, .tile-nav.can-scroll::after {
    color: #FFF;
    background-color: #000000;
    transition-delay: 0s;
    transition-duration: 0.25s;
    transition-timing-function: ease-in-out;
    box-shadow: 0 0 16px -2px rgba(255,255,255,.125)
  }
  .tile-nav{
    transition-delay: 0s;
  }
  .zci__main.has-tiles, .zci__main.has-tiles--grid{
    transition-duration: .25s;
    transition-timing-function: ease-out;
  }
  .star::after{
    color: #000000
  }
  .badge--official {
    background-color: #000;
    color: #FFF
  }

