css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pcars2.wmdportal.com"), domain("forum.projectcarsgame.com"), domain("forum.wmdportal.com"){
	/* Background Color */
	body {
		background: #1b1b1b !important;
		color: black !important;
		margin: 0px 10px !important;
	}
	.body_wrapper {
	/*padding:0 !important;*/
		background-color:black !important;
	}
	html {
		background-color:black !important;
		background-image:none !important;
		text-decoration-color: white !important;
	}/* Text Color*/
	[style="color:#003399;"]{
		color: #2563ce !important;
	}
	#pagetitle,div, li, legend, dt, input#tag_add_input, h3, .primary.full.textbox, .cke_dialog_ui_labeled_label, .cke_dialog_ui_html, #cke_39_label, #uploadtabs0 > p, h2,.rating, input.button, label, select.primary {
		color: #bbbbbb !important;
	}
	#title.primary.textbox.full, select.rating, textarea{
		color: black !important;
	}
	.restore , #threadrating_current > DIV > b, #threadrating_current > label, div.forumhead > *  , .bbcode_code{
		color: #eeeeee !important;
	}
	.usertitle, dd{
		color: #bbbbbb !important;
	}
	a, a:link {
		color: #ddeeff!important;
	}
	span.viewing{
		color: #a0a0a0 !important;
	}
	.postcontent.lastedited, span.time, p.lastpostdate{
		color: #efefef !important;
	}
	/* Background Color */
	#yui-gen4.popupctrl, #yui-gen7.popupctrl, #yui-gen10.popupctrl {
		background: black !important;
	}
	.rating0.sticky, .rating1.sticky, .rating2.sticky, .rating3.sticky, .rating4.sticky, .rating5.sticky, .rating0.sticky >  .threadpostedin.td.alt, .rating1.sticky >  .threadpostedin.td.alt, .rating2.sticky >  .threadpostedin.td.alt, .rating3.sticky >  .threadpostedin.td.alt, .rating4.sticky >  .threadpostedin.td.alt, .rating5.sticky >  .threadpostedin.td.alt {
		background: #393939  !important;
	}
	 .threadstats.td.alt{
		 background: #252525  !important;
	 }
	.popupbody *, .popupbody, p.nonthread, dd.userprof_moduleinactive, .cke_dialog_body, .blockfoot, .threadpostedin.td.alt {
		background: #282828  !important;
		border:0 !important;
	}
	.signature.restore, .thread_controls.toolsmenu > DIV ,.popupctrl, .above_threadlist_controls.toolsmenu > DIV{
		background: #282828 !important;
	}
	#fileupload1.textbox, .primary.textbox{
		background: white !important;
    }
	.forumhead,.restore, #threadrating_current, div.forumhead > * , div.threadlisthead.table{
	background: #9e6d10 !important;
    }
	.postbody, .editor.quickreply, .blockfoot.actionbuttons, .editor,  #navtabs, .navtab, #yui-gen11 > DL > DD > SPAN, .blocksubhead, .inner_block, .thread_info_block, #forum_display_options, #icon_legends, .forum_info_block,
	.nonsticky, .sticky, #wgo > DIV, .sgicon, .maincol, #recentalbums > DIV, #albumlist > DIV,  .forumrow, #buddylist > li, .pagetitleinfo.textcontrol, .underblock, .pagination > DD > SPAN * , .postcontent, .attachments{
		background:#282828 !important;
	}
	#navtabs > LI:first-child + LI > UL, .buttoncontainer, .searchbutton, .textboxcontainer, #thread_controls, .selected > .navtab, #navtabs > LI:first-child + LI + LI > UL, #navtabs > LI:first-child + LI + LI + LI > UL, #navtabs > 		LI:last-child > UL, #navtabs > LI:first-child > UL, #yui-gen11, #pollresults, .threadinfohead, .optiontitle, #footer, #above_threadlist_controls, #wgo > H2, .albumlist_entry, #latestalbumcontainer > LI{
		background:black!important;
	}
	.postdetails, .userinfo, .bbcode_quote, .blockbody.formcontrols, .blockrow, .blockbody.postpreview, .block.vbform, .block.smaller, #pollinfo, #blogentries, .blockbody, .block,  .threadlastpost.td.alt,
	#forum_icon_legend, .blogcontent, .actionbuttons, .postbit-lite, .texteditor, .selected_view_section, .primary{
		background:#111111!important;
	}
	.postcontrols  a  {
		background-color:#5a5a5a !important;
	}
	.postlinking a  {
		background-color:transparent !important;
	}
	.posthead, .blockhead, .postbitlegacy.postbitim.postcontainer,  .threadlisthead.table, #forumbits > DIV, #forum_icon_legend > H5 , .bbcode_code{
		background:#5a5a5a none !important;
	}
	.textcontrols, .cke_dialog_tab, .cke_toolgroup {
		background:#5a5a5a !important;
	}
	#qr_defaultcontainer, .navlinks, .blockfoot.settings_form_top_border, #latest_infractions > DIV, .blockfoot.controlbar.textpad, #threadlist > FORM > DIV:last-child, .member_summary{
		background:none !important;
	}
	.pagination.popupmenu.nohovermenu * {
		background:transparent !important;
	}
	dl.stats.userprof_content, a.textcontrol, .cke_dialog_contents, .button, .userinfo_noavatar{
		background: #111111 !important;
	}
}
