css:
@-moz-document url-prefix("http://iichan.hk/") {
html {	background:url("data:image/png;base64,") bottom repeat-x;
	background-color:#BBB;
	color:#333;
	font-size:10pt!important;
	min-height:100%;
	font-family:"Trebuchet MS",Trebuchet,serif;
	margin:0;
	padding:0 0 0 1px;
}
body {
	background:rgba(0,0,0,0);
	color:#333;
	margin:0 1% 84px;
	padding:47px 21px 9px 22px;
	background-image:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAACCAYAAABhYU3QAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuNWWFMmUAAAAgSURBVBhXY/j//z9WDAT8QKwHxNFAPAOrIhBGVcgwAwAp9SY9AdJGCQAAAABJRU5ErkJggg=="),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAACCAYAAACKVvbTAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuNWWFMmUAAAAfSURBVBhXYwCCGUAcDcR6QMz///9/BnQMAgQU/WcAAOUyIEO5mIpRAAAAAElFTkSuQmCC"),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAIAAACQd1PeAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuNWWFMmUAAAAMSURBVBhXY3j37h0ABZgCy4Oh2ogAAAAASUVORK5CYII=");
	background-position:left,right,center;
	background-repeat:repeat-y,repeat-y,no-repeat;
	background-size:auto,auto,calc(100% - 19px) 100%;
}
body:after {
	display:block;
	content:"";
	position:absolute;
	left:1px;
	padding:93px 0 0 98%;
	margin:0 1%;
	background-image:url("data:image/png;base64,"),url("data:image/png;base64,"),url("data:image/png;base64,");
	background-position:left bottom,center bottom,right bottom;
	background-repeat:no-repeat;
	background-size:20px 84px,calc(100% - 40px) 84px,20px 84px;
}
body:before {
	display:block;
	content:"";
	position:absolute;
	left:11px;
	right:9px;
	top:0;
	padding:34px 0 0;
	margin:0 1%;
	background-image:url("data:image/png;base64,");
	background-repeat:repeat-x;
	background-position:top center;
}
a,.spoiler:hover a {
	color:#F60;
}
a:visited {
	color:#F60;
}
a:hover,.spoiler:hover a:hover {
	color:#06F;
}
.adminbar {
	clear:both;
	float:none;
	text-align:left;
}
.logo {
	clear:both;
	text-align:left;
	font-size:2em;
	font-weight:bold;
	color:#F60;
	width:100%;
	padding:3px 2px;
	display:inline-block;
}
.banner {
	margin:0;
}
.theader,.passvalid {
	background:#DDD;
	text-align:center;
	padding:2px;
	color:#26A;
	clear:both;
	font-weight:bold;
	margin-bottom:.5em;
	border:solid 1px #CCC;
	-moz-border-radius:5px;
	-webkit-border-bottom-left-radius:5px;
	-webkit-border-bottom-right-radius:5px;
	-webkit-border-top-left-radius:5px;
	-webkit-border-top-right-radius:5px;
}
.postblock {
	background:transparent;
	color:#024;
	font-weight:bold;
}
.footer {
	display:none;
}
.dellist {
	font-weight:bold;
	text-align:center;
}
.delbuttons {
	text-align:center;
	padding-bottom:4px;
}
.managehead {
	background:#DDD;
	color:#024;
	padding:0px;
}
.postlists {
	background:#FFF;
	width:100%;
	padding:0px;
	color:#800000;
}
.row1 {
	background:#DDD;
	color:#024;
}
.row2 {
	background:#CCC;
	color:#002244;
}
.unkfunc {
	background:inherit;
	color:#789922;
	margin:0!important;
}
.reflink {
	font-size:.8em;
	font-weight:bold;
}
.filesize {
	text-decoration:none;
	color:#666;
	font-size:.8em;
}
.filesize em,.de-refmap {
	font-style:normal;
}
.filetitle {
	background:inherit;
	font-size:1.2em;
	color:#024;
	font-weight:bold;
}
.postername,.commentpostername,.adminname {
	color:#004A99;
	font-weight:bold;
}
.postertrip {
	color:#F30;
}
.oldpost {
	color:#CC1105;
	font-weight:bold;
}
.omittedposts,.abbrev {
	color:#666;
}
.greeting {
	color:#000;
}
.header {
	background:#CCC;
	color:#333;
	font-size:100%;
	padding:2px 2px 2px 8px;
	text-align:left;
	background-repeat:repeat-x;
}
.list {
	border:none;
	background:#eee;
}
.list div a {
	display:block;
	color:#555;
	padding:0 0 0 3px;
	text-decoration:none;
}
.category {
	margin:1em 0 0 0;
}
form[de-form] table,form[de-form] table tbody,#delform table,#delform table tbody {
    display:block;
}
.reply {
	padding-right:3px;
	background:#DDD;
	color:#333;
	border:solid 1px #CCC;
	padding:0;
	padding-right:2px;
	-moz-border-radius:5px;
	-webkit-border-bottom-left-radius:5px;
	-webkit-border-bottom-right-radius:5px;
	-webkit-border-top-left-radius:5px;
	-webkit-border-top-right-radius:5px;
}
blockquote {
	margin:.5em .5em .5em 1em;
}
blockquote p {
	padding:0;
}
blockquote>*:first-child {
	margin-top:0;
}
blockquote>*:last-child {
	margin-bottom:0;
}
blockquote>blockquote+* {
	margin-top:0;
}
blockquote>.unkfunc+.unkfunc {
	margin-top:1em!important;
}
blockquote>*:not(blockquote)+.unkfunc {
	margin-top:-1em!important;
}
.reply blockquote {
	margin:.5em;
}
.spoiler,.spoiler a {
	color:#000;
	background:#000;
}
.spoiler:hover {
	color:#fff!important;
	background:#000;
}
/*[[spoiler]]*/
.doubledash {
	display:none;
}
.replytitle {
	font-size:1.2em;
	color:#024;
	font-weight:bold;
}
.thumbnailmsg {
	font-size:.8em;
	color:#666;
}
hr {
	border-style:solid none none none;
	border-width:1px;
	border-color:#BBB;
}
table {
	border-style:none;
}
table td {
	border-style:none;
}
.nothumb {
	background-color:#FFF;
	border-style:dotted;
	margin:.3em .5em;
}
.highlight {
	background:#EEDACB;
	color:#333;
	border:2px dashed #E60;
}
form {
	margin-bottom:0;
}
.postarea {
	text-align:center;
}
.postarea table {
	margin:0 auto;
	text-align:left;
}
.thumb,.de-img-full {
	border:none;
	float:left;
	margin:2px 20px;
}
.de-img-full {
	max-width:calc(100% - 40px);
	height:auto!important;
}
pre {
	font-family:Consolas,Lucida Console,monospace!important;
	margin:0;
}
code {
	display:inline-block;
	background:#EFEFEF;
	width:auto;
	padding:0;
	margin:0;
}
pre>code {
	border:#666 dashed 1px;
	max-height:400px;
	overflow:auto;
}
p>code {
	border:#DFDFDF dashed 1px;
}
.de-pview {
	margin:0;
	padding:0;
	border:1px #666 dashed!important;
	-webkit-box-shadow:#666 0 0 10px;
	-moz-border-radius:5px;
	-webkit-border-bottom-left-radius:5px;
	-webkit-border-bottom-right-radius:5px;
	-webkit-border-top-left-radius:5px;
	-webkit-border-top-right-radius:5px;
}
.nothumb {
	float:left;
	background:#eee;
	border:2px dashed #aaa;
	text-align:center;
	margin:2px 20px;
	padding:1em .5em 1em .5em;
}
.reflink a {
	color:#333;
	text-decoration:none;
}
.reflink a:hover {
	color:#800000;
}
.reflink a:visited {
	color:#333;
}
.reply .filesize {
	margin-left:20px;
}
.de-refmap {
	font-size:11px;
	margin:.5em;
}
.userdelete {
	float:right;
	text-align:center;
	white-space:nowrap;
}
.replypage .replylink {
	display:none;
}
.pagelist {
	max-width:600px;
}
.adminname:after {
	content:" ## Админ ##";
	color:#800080;
	font-weight:normal;
}
.de-thread-updater {
	font-size:0!important;
}
.de-thread-updater>* {
	font-size:10pt!important;
}
.de-thread-updeter:before {
	content:"";
}
.de-thread-updater>.de-abtn {
	text-decoration:underline!important;
}
.de-thread-updater>.de-abtn:after {
	content:" Получить новые посты (если есть)"!important;
	font-size:10pt!important;
}
.de-thread-buttons::before {
	display:none;
}
.de-thread-updater>.de-abtn:before {
	content:url("data:image/gif;base64,");
}
.de-btn-expthr,.de-btn-fav,.de-btn-fav-sel,.de-btn-hide,.de-btn-hide-user,.de-btn-rep,.de-btn-src,.de-btn-stick,.de-btn-stick-on {
	fill:#aba8a6!important;
	color:#fff!important;
	-webkit-transition:fill .3s;
	-moz-transition:fill .3s;
	-ms-transition:fill .3s;
	-o-transition:fill .3s;
	transition:fill .3s;
}
.de-btn-expthr:hover,.de-btn-fav:hover,.de-btn-fav-sel:hover,.de-btn-hide:hover,.de-btn-hide-user:hover,.de-btn-unhide:hover,.de-btn-unhide-user:hover,.de-btn-rep:hover,.de-btn-src:hover,.de-btn-stick:hover,.de-btn-stick-on:hover {
	fill:#a75!important
}
.de-win-body {
	background:#eee!important;
}
#de-panel:lang(de) {
	background:#ABA8A6
}
#de-panel-btns:lang(de)>li>a:hover {
	border-color:#666;
}
#de-content-hid,#de-content-fav,#de-content-vid {
	background:#DDD!important;
	border:1px solid #CCC!important;
	border-radius:5px;
	font-size:10pt;
	padding:0 3px;
}
#de-content-hid>b,#de-content-fav>b,#de-content-vid>b {
	width:100%;
	display:inline-block;
	text-align:center;
}
.de-cfg-head,#de-cfg-bar {
	background:transparent!important;
	color:#333!important;
}
.de-cfg-tab-back:lang(de),.de-cfg-tab-back:lang(en),.de-cfg-tab-back:lang(fr),.de-cfg-body:lang(de),#de-cfg-btns:lang(de),.de-cfg-body:lang(en),#de-cfg-btns:lang(en),.de-cfg-body:lang(fr),#de-cfg-btns:lang(fr),.de-cfg-head {
	border-color:#CCC!important;
}
.de-cfg-head {
	border:1px solid #ccc;
	border-bottom:none;
}
.de-fav-inf-new {
	color:red!important;
}
.de-fav-inf-posts {
	font-size:.9em;
	font-family:"Trebuchet MS",Trebuchet,serif;
}
.de-entry {
	border:none;
	font-size:.8em;
	padding:0 3px;
}
.de-entry>input,.de-content-block>input {
	margin:0!important;
}
.de-thr-hid {
	background:rgba(0,0,0,0)!important;
	border:none;
}
div[de-thread]+br {
	display:none;
}
.de-qarea-hanging>#de-pform,.de-qarea-hanging>.de-cfg-head {
	border:none!important;
}
#de-qarea.de-qarea-hanging {
	background:#eee;
	padding:3px;
	border-radius:8px;
	opacity:.85;
	-webkit-transition:opacity .2s;
	-moz-transition:opacity .2s;
	-ms-transition:opacity .2s;
	-o-transition:opacity .2s;
	transition:opacity .2s;
	margin:0;
	border:1px #666 dashed!important;
	box-shadow:#666 0 0 10px;
}
#de-qarea.de-qarea-hanging:hover {
	opacity:1;
}
#de-qarea.de-qarea-inline {
	border-radius:8px;
	border:1px #666 dashed!important;
	box-shadow:#666 0 0 10px;
}
#de-qarea #de-qarea-utils>span:hover {
	color:#06f!important;
}
.de-selected,.de-error-key {
    box-shadow:4px 0 0 0 #f60,-4px 0 0 0 #f60;
}
.de-new-post {
    box-shadow:4px 0 0 0 #06f,-4px 0 0 0 #06f;
}
#de-alert {
	top:40px!important;
	width:calc(40% + 60px)!important;
	left:30%!important;
	margin-left:-10px;
	padding-right:18px;
}
#de-alert>div {
	text-align:center!important;
	box-shadow:rgb(102, 102, 102) 0 0 10px;
	border:solid 1px #CCC!important;
	float:none!important;
	padding:20px 0!important;
	margin:10px!important;
	color:#004A99;
	font:inherit!important;
	font-weight:bold!important;
	width:calc(100% - 2px)!important;
	line-height:16px!important;
}
.de-alert-btn:not(.de-wait)+div {
	margin:0!important;
}
#de-txta-resizer {
	display:none!important;
}
#de-txt-panel button {
	font-family:'Trebuchet MS';
	font-size:0;
	border-radius:4px;
	border:1px solid #ccc;
	background:#f6f6f6;
	text-decoration:none;
	padding:3px 4px;
	color:#f60;
	cursor:pointer;
	margin-right:1.2px;
}
#de-txt-panel button:after {
	font-size:12px;
}
#de-btn-bold button:after {
	content:"Жирный";
}
#de-btn-italic button:after {
	content:"Курсив";
	font-style:italic;
}
#de-btn-strike button:after {
	content:"Зачёркнутый";
	text-decoration:line-through;
}
#de-btn-spoil button:after {
	content:"Спойлер";
}
#de-btn-code button:after {
	content:"Код ▼";
	height:22px;
	line-height:14px;
}
#de-btn-quote button:after {
	content:"Цитата";
}
#de-txt-panel button:hover {
	color:#c77405;
	border-color:#fbcb09;
	background:#fdf5ce;
}
.de-textarea,.de-qarea-hanging .de-textarea {
	min-width:347px!important;
}
#postform img[src*="captcha"] {
  -webkit-filter:grayscale(100%);
  -moz-filter:grayscale(100%);
  -ms-filter:grayscale(100%);
  -o-filter:grayscale(100%);
  filter:grayscale(100%);
  filter:gray;
}
}

@-moz-document url-prefix("http://iichan.hk/n/") {
html {
	background-attachment:fixed;
	padding:0;
}
a {
	color:#555!important;
}
body {
	font-family:sans-serif;
	background:none;
	background-color:transparent;
	color:#000;
	margin:8px 0 0 0;
	padding:0;
	width:90%;
}
body:after,body:before {
	content:none;
}
.adminbar,.logo,.news,hr {
	display:none;
}
div[id^="thread"] {
	background:url("data:image/png;base64,") top repeat-x #eee;
	margin-left:36px;
	padding:4px 0 0 4px;
	border:solid 1px #666;
	font-weight:bold;
	font-size:9pt;
	color:#333;
}
div[id^="thread"]>a[href*="/src/"] {
	display:none;
}
div[id^="thread"]>blockquote {
	font-weight:initial;
	margin:9px 5px 5px 1px;
	color:#000;
}
.postername,.adminname {
	color:#333!important;
}
.adminname:after {
	content:none;
}
.postername:before {
	content:"by ";
}
.postername:after {
	content:" - ";
}
.filetitle {
	background:none;
	font-size:9pt;
	color:#333;
}
table[border="1"] {
	border:solid 1px #666;
	background:#eee;
	margin-left:36px;
}
}

@-moz-document url-prefix("http://iichan.hk/n/list") {
body {
	margin:7px;
	padding:0;
}
.header,.logo {
	background-image:url("data:image/png;base64,");
}
.logo a,.sublogo a,.adminbar a {
	text-decoration:none;
}
.logo {
	display:block;
	font-size:10pt;
	padding:0;
	position:absolute;
	top:8px;
	padding-left:1px;
	width:172px;
	line-height:22px;
}
.logo a {
	color:#555;
	font-size:150%;
}
.adminbar br {
	display:none;
}
.adminbar {
	margin-top:30px;
	width:170px;
	background:#eee;
	font-size:0;
	padding-left:3px;
	display:block;
}
.list div a:hover,.adminbar:hover {
	background:#F6F6F6;
}
.adminbar a[href$="_ru.html"],.sublogo {
	font-size:0!important;
}
.adminbar a[href$="_ru.html"]:before {
	content:"Язык:";
	pointer-events:none;
	color:#000!important;
}
.adminbar a[href$="_ru.html"]:after{
	content:" ru";
}
.adminbar a {
	margin-right:3px;
}
.sublogo {
	padding:0;
	text-align:left;
	width:173px;
	background:#eee;
}
.sublogo a {
	padding-left:3px;
	color:#555;
	display:block;
	width:170px;
	text-align:left;
}
.sublogo a:hover {
	background:#F6F6F6;
}
.sublogo a[href*="twitter"],.sublogo a[href*="plus"],.sublogo a[href*="gplus"] {
	display:none;
}
.category,.adminbar a,.sublogo a,.adminbar a[href$="_ru.html"]:before,.adminbar a[href$="_ru.html"]:after {
	font-size:9pt!important;
	color:#555;
}
}

@-moz-document url-prefix("http://iichan.hk/cgi-bin/paint.pl"), url-prefix("http://iichan.hk/p/"), regexp("(http://iichan.hk/)\\w+(/src/).*") {
* {
	margin:0!important;
	padding:0!important;
	border-spacing:0!important;
	border:0!important;
	background:none!important;
}
body:before,body:after {
	content:none;
}
}
