css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pikabu.ru") {
  .header-cont{
    background: #F9F9FB !important;
    
  }
  #logo{
    display: none;
  }
  #logodiv{
    background: url('http://i.imgur.com/AjIgxoo.png'), no-repeat;
    width: 267px;
    height: 86px;
  }
  td.header-nav-bottom > ul.menu, table#header_t ul li{
    height: auto;
  }
  
  table#header_t .menu li a, table#header_t .menu .active2 a, table#header_t .menu .active2 a:hover, table#header_t .menu .active a, table#header_t .menu .active a:hover{
    border: 1px solid #32D257;
    border-radius: 20px;
    padding: 10px 15px;
    font-size: 16px;
    line-height: 16px;
  }
  table#header_t .menu li a{
    color: #32D257;
    margin: 0 auto;
  }
  table#header_t .menu li a:hover{
    
  }
  
  table#header_t .menu .active2 a, table#header_t .menu .active2 a:hover, table#header_t .menu .active a, table#header_t .menu .active a:hover{
    background: #05BF54;
    margin: 0 auto;
  }
  table#header_t .active, table#header_t .active a{
    margin: 0;
  }
  td.header-nav-bottom > ul.menu > li.menu-item-default > a{
    position: relative;
  }
  td.header-nav-bottom > ul.menu > li.menu-item-default{
    width: auto;
  }
  table#header_t .noactive{
    margin: 0;
  }
  .header-nav tbody tr:nth-child(1){
    position: absolute;
    background: white;
  }
  .header-nav tbody tr:nth-child(2){
    position: absolute;
    top: 22px;
    width: 100% !important;
  }
  
  .header-nav-bottom, table#header_t .menu{
    width: 100% !important;
    display: table;
    
  }
  table#header_t .menu{
    margin-left: 30px;
  }
  .header-nav-bottom > .menu > li {
    width: 11% !important;
    height: 43px !important;
    display: table;
  }
  .header-nav-bottom > .menu > li > a{
    display: table;
    margin: 0 auto;
    display: table;
  }
  /* ------------- serach_block ------------- */
  #serach_block{
    position: absolute;
    top: 24px !important;
    right: 24px !important;
    z-index: 999;
    margin: 0;
    padding: 0;
  }
  .header_links.header-nav-top{
    position: absolute;
  }
  .search_block{
    border: 1px solid #CACACA;
    border-radius: 5px !important;
    background: #FFF url('http://i.imgur.com/PAbtC43.png') right 5px center no-repeat;
    
    color: #9B9B9B;
    margin: 0;
    padding: 5px 35px 5px 15px;
  }
  .search_block:focus, .search_block{
    box-shadow: inset 0 0 5px 0 solid rgba(0,0,0,0.5) !important;
  }
  div.header-search-box > form > div{
    position: relative;
  }
  div.header-search-box > form > div > input[type=image]{
    display: none;
/*     position: absolute;
    right: 0;
    top: 0; */
/*     background-image: url('http://i.imgur.com/PAbtC43.png'); */
  }
}
