css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://howfile.com/file/") {

.crcl_header > div:nth-last-of-type(2),
.crcl_content > .content_body > div:nth-last-of-type(2),
.row1_left div[style="float:left; padding-left: 16px;"],
div[style="width:640px;height:22px;float:left;padding-left:16px;"],
#tu,
.btn1,
a[href*="/premiumMembership/"],
#downloadtable > tbody > tr:not(:last-of-type),
#downloadtable ~ .h3_title,
#downloadtable ~ div:last-of-type {
display: none !important;
}

#floatdiv  {
display: block !important;
position: static !important;
border: none !important;
padding: 0 !important;
background: transparent !important;
}

#downloadtable,
#downloadtable td {
border: none !important;
}

}
