css:
@-moz-document domain("twitch.tv") {
/* Created by treasury - google.com/+ViktorKincses - http://twitch.tv/treasury */
/* Style URL: https://userstyles.org/styles/117524/treasury-s-twitch-edge-to-edge-video-dark-new */
.svg-logo_glitch path{fill:#97939f!important;}
#channel{padding: 0 0;}
.home_icon{margin:0px -10px !important}
@keyframes out{0%{padding-left:30px;} 90%{padding-left:30px;} 100%{padding-left:0px;}}
.closed .warp__item>span, .closed .warp__item>a{padding:1rem 0;}
#left_col.closed #bttv-follow-count{left:13px;}
#left_col.closed, #left_col.closed .warp{width:30px;height:375px!important;min-height:375px;display:block;overflow:hidden;}
.warp__scroll{height:calc(375px - 4.5rem);}
#left_col.closed .sep{margin:0;padding:0;}
#left_col.closed nav{margin-left:-30px;background:#282828;}
.closed .warp__item>span, .closed .warp__item>a{padding: 0.9rem 0;}
#left_col.closed:hover{padding-left:30px;transition: 0.2s padding-left;}
.warp__item--anchor{padding:0.5rem 0 0;}
.warp__drawer,.warp__item--anchor{padding-bottom:0rem;background:#444;}
.warp .arrow:before{border-top: 2px solid rgba(255,255,255,.7);border-left: 2px solid rgba(255,255,255,.7);}
.warp__drawer .pd-t-1 {padding-top: 0.3rem;}
.warp__drawer .warp__item{margin-top:-3px;margin-bottom:-3px;}
#main_col.expandLeft, #flash.expandLeft{margin-left:0px;}
.editable{margin:6px 280px 6px 18px !important;position:absolute!important; top:0; left:0;width:calc(100% - 345px);z-index:11}
#small_nav .content .divider,#small_nav .content ul #small_account:before, #small_nav .content ul #small_login:before{margin:0 0!important;}
.profile-photo{background-color:transparent!important;border-color:transparent!important;}
#channel .player-column #broadcast-meta .info .title{color:#777!important;}
.info .channel{z-index:2;}
.info .channel a{opacity:0.9;}
.chat-header{background:#323232;color:#888;box-shadow:none!important;}
.room-title{padding-top:1px;}
.ember-chat .chat-messages{background:#1E1E1E;}
.chat-messages .message,.chat-messages .timestamp,.chat-messages .colon{color:#8c8c9c;}
.chat-interface{background:#282828;border-top: 1px solid #333;}
#chat_text_input, #chat_text_login, .ember-chat .chat-interface textarea{background:#141414;border: 1px solid #333;box-shadow:none!important;color:#D3D3D3;}
.open #left_close{background:#f60!important;opacity:1;right:5px;width:30px;height:30px;}
#right_close{top:0px;right:0px;width:12px;height:16px;}
.closed #left_close{opacity:0.2;z-index:15!important;transition: opacity 0.3s;top:0px;right:18px;width:12px;height:16px;} 
#right_close{opacity:0.2;z-index:10000!important;transition: opacity 0.3s;}
.closed #left_close:hover,#right_close:hover{opacity:0.6!important;}
.chat-container, .ember-chat-container,
.ember-view.ember-chat.roomMode{background:transparent!important;}
#channel .player-column #broadcast-meta .info .edit-link span,.follow-button:not(.ember-follow) .follow,.theatre-button{color:#666;opacity:0.8;}
.button.glyph-only svg path,.emoticon-selector-toggle svg path{fill:#666!important;}
.emoticon-selector-toggle{opacity:0.6;}
.profile-link{opacity:0.9;}
.primary_button, .primary.button{border:none;}
.svg-roomlist{margin-top:5px!important;}
.dynamic-player, .dynamic-player object, .dynamic-player video{width:100%!important;}
.ember-chat .chat-interface{padding:10px;}
.ember-chat .chat-interface .textarea-contain{top:0;margin:0}
.chat-buttons-container{padding-top:10px;margin:0;}
.player-column.host-frame.js-host-frame #channel_panels_contain{position:absolute !important;top: calc(100% + 10px) !important;width:100%!important;text-align:center;}  
.player-column.host-frame.js-host-frame.inactive #channel_panels_contain{position:relative !important;width:100%!important;}
#channel .player-column #player{top:72px !important;position:absolute !important;width: 100% !important;height: calc(100% - 72px) !important;z-index: 2;}
#channel .player-column #video-1,.dynamic-player object{position:absolute !important;width: 100% !important;height: 100% !important;}#treasuryTV{z-index:99;}
#channel .player-column #hostmode #player{top:0px !important;margin-left:0px !important;position:relative !important;width: 100% !important;height:100%!important;z-index: 2;}
.app-main.theatre .js-player{top:0px !important;margin-left:0px !important;}
.app-main.theatre #channel .player-column #player{top:0px !important;margin-left:0px !important;position:absolute !important;width: 100% !important;height: 100% !important;z-index:6;}
.stats-and-actions{padding:10px 16px 0 0;position:absolute!important;top:0px; right:0px;border:0;background:#000;width:100%;height:72px;z-index:10!important;}
.player-column, .host-frame{background:#000;height:72px;}
.channel-actions{position:absolute!important;top:10px; right:10px;z-index:9!important;}
.channel-stats{position:absolute!important;top:40px; right:20px;z-index:6!important;}
.button.drop{opacity:0.9;padding-right:0!important;}
.button.drop:hover{opacity:1;text-decoration: underline;}
.svg-glyph_followers path,.svg-glyph_views path{fill:#666!important;}
.theatre-button{display:none;}
.popup{top:35px!important;left:inherit!important;right:5px!important;z-index:11 !important;}
.popup .dropmenu{padding:20px!important;z-index:11 !important;}
.notify-menu{z-index:11!important;position:absolute;bottom:inherit!important;top:35px!important;left:inherit!important;right:5px!important;opacity:1!important;}
a[href="/treasury/profile"]{color:#f60;}
.channel-actions .follow-button.ember-follow:not(.is-following),.channel-actions .action{margin-right:10px;}
.js-options-dropdown{top:35px!important;left:inherit!important;right:5px!important;}
.follow-button,.toggle-notification-menu,.subscribe-button,.svg-glyph_live{opacity:0.45;transition: opacity 0.3s;}
.follow-button:hover,.toggle-notification-menu:hover,.subscribe-button:hover,.svg-glyph_live:hover{opacity:1;}
.upsell-banner{margin:110px 36px;padding:10px;background: rgba(0,0,0,0.6);z-index:5;color:#ddd;}
.upsell-banner h1{color:#ccc;}
#panels-edit-contain{position:absolute;top:calc(100% + 20px);left:30px;z-index:4;}
.panel.masonry-brick{background-color:transparent!important;border:0px transparent!important;}
.button.drop:after{display:none!important;}
.hostmode-title-container {width:350px;background:transparent;position:absolute;top:0px;left:calc(50% - 175px);z-index:20!important;color:#8c8c8c;}
.host-profile-image{opacity:0.9;}
.hostmode-title .what{opacity:0.9;}
.hostmode-title .float-left{max-width:250px;overflow:hidden;white-space:nowrap!important;}
.hostmode-title{background:rgba(0,0,0,0.6)!important;margin-top:16px!important}
#hostmode{padding-top:72px!important;height:calc(100% - 72px);}
.target-meta{position:absolute;top:24px;left:16px;margin:0!important;color:#777;}
.close-hostmode{z-index:22!important;padding:6px 0px;background:rgba(0,0,0,0.6);position:absolute!important;bottom:0px;width:220px!important;left:calc(50% - 110px)!important;}
#channel .player-column #hostmode{background:#000;}
.expandRight .title a[href="/treasury"]{background:#fa3!important;color:#000!important;position:absolute;left:0px;top:156px;padding:10px 0px 28px 2px;width:calc(100% - 22px);overflow:hidden;text-overflow: ellipsis;}
.following-list a[href="/treasury/profile"]{color:#000!important;}
.expandRight .streams .meta .info{position:absolute;bottom:-10px;left:2px;color:#666!important;width:241px;overflow:hidden;}
.expandRight .streams .meta{height:24px;}
/*.expandRight .meta .desc{margin-top:22px;margin-right:20px;}*/
.expandRight .streams .stream{min-height:201px;width:297px!important;margin-bottom:40px!important;}
.expandRight .videos .video{min-height:223px;}
.following-list .name a[href="http://www.twitch.tv/treasury"]{position:absolute;left:50px!important;padding-left:10px;background:#fa3;width:100%;height:40px;color:#000!important;z-index:1;}
.following-list .game{position:absolute;left:60px;top:28px;width:190px;z-index:2;}
.following-list .channel{position:relative;}
.following-list .right{z-index:2!important;position:absolute;right:10px;}
#nav_advertisement,.fp-banner-ad,#sizzle_strip,.advertisement,.new_advertisement,.js-new-channel-ad,#ad_actions,.ad_leader,.fp_ad,.promoted-game{display:none!important;}
.send-chat-button{opacity:0.45!important;margin-right:1px;transition: opacity 0.3s;}
.send-chat-button:hover,#emote-menu-button:hover{opacity:1!important;}
#emote-menu-button{opacity:0.45!important;transition: opacity 0.3s;}
.chat-buttons-container{margin:0 0!important;}
.chat-buttons-container .svg-gear{margin-left:1px;}
.send-chat-button{min-width: 100px;}
.chat-room{top:0px!important;}
.chat-header{display:none;}
.chatters .chat-header{display:block;}
.viewall a{height:156px!important;padding-bottom:0px!important;}
.chat-container .loading-mask{background:#1E1E1E;}
span.from[style*="color: #0000FF"]{color:#06f!important;}
span.from[style*="color: #00FF7F"]{color:#0c6!important;}
.message .emoticon,.message-line .moderator{opacity:0.9!important;}
.ember-chat .chat-messages .chat-line{padding:6px 10px;}
#right_col.open{width:320px!important;}
#main_col, #flash{margin-right:320px;}
#main_col.expandRight, #flash.expandRight{margin-right:0px!important;}
.player-controls-bottom {padding-top:2.5em;background:-webkit-linear-gradient(bottom,rgba(0,0,0,.8) 0,rgba(0,0,0,.3) 60%,transparent 100%);background:linear-gradient(to top,rgba(0,0,0,.8) 0,rgba(0,0,0,.3) 60%,transparent 100%);}
.emote-menu .single-row .emote-container{height:78px;}
.emote-menu .single-row .emote{margin-bottom:0px;}
#starred-emotes-group{background:#202020;}
.offlineChannelStatus{box-shadow:none;background:transparent;padding:0;margin:0;position:absolute;left:16px;top:3px;color:#666;}
.target-title{line-height:110%!important;margin-top:-1px;}
.what--offlineChannelStatus{display:none;}
.conversations-content{left:78px;right:78px;}
@keyframes convOut{0%{margin-top:0px;opacity:1} 90%{margin-top:0px;opacity:1} 100%{margin-top:27px;opacity:0.3}}
.conversations-list-container{border:1px solid #666;margin-top:27px;opacity:0.3;animation-name:convOut;animation-duration:7s;transition: 0.2s all;}
.conversations-list-container:hover,.list-displayed{margin-top:0px;opacity:1;border-color:transparent;}
}
