css:
@-moz-document domain("ctext.org") {
/* Palette generated by Material Palette - materialpalette.com/cyan/pink */

.dark-primary-color    { background: #0097A7; }
.default-primary-color { background: #00BCD4; }
.light-primary-color   { background: #B2EBF2; }
.text-primary-color    { color: #FFFFFF; }
.accent-color          { background: #FF4081; }
.primary-text-color    { color: #212121; }
.secondary-text-color  { color: #727272; }
.divider-color         { border-color: #B6B6B6; }


body {
	color:rgba(0,0,0,0.87);
	background:#f9f9f9;
  	font: 13px/25px Roboto,noto sans cjk tc light,HanaMinA,HanaMinB;
    /*letter-spacing:0.01em;*/
} 
/*Line-height = Font-size + Leading/2*/

input,select,.ctext,.etext,.annotation,.htmltext {font-family:inherit}

a {
	text-decoration:none;
	color:#00bcd4!important
}
a:hover, #language a:hover {
    color: #FF4081!important;
}

	::-webkit-scrollbar {;
	width:7px;
	height:0;
	background:none;
	border:none
}

::-webkit-scrollbar-thumb {
	border-radius:5px;
	background-color:rgba(0,0,0,0.3);
}

#titleline {font-size:27px;position:fixed;top:8px;line-height:1em;}
#titleline * {background:none;padding:0;}
#titleline td:last-child {position:relative;left:2.125em;}
.sprite-logo {
    height:2em;
    width:2em;
    background:none;   
    position:absolute;
    top:0;
}
#titleline font {font:inherit}
#titleline .etext {font-size:0.62em}
.sprite-logo div::after {
	all:initial;
	content:"诸百子家";
    font:700 1em/1em noto sans cjk tc;
    color:#fff;
	background:#FF4081;
    direction:rtl;
    unicode-bidi: bidi-override; 
	height:inherit;
	width:inherit;
    border-radius:100%;
    display:block;
	overflow:hidden;
}

#menubar {
	background-color:#00BCD4;
	width:15%;
    min-width:200px;
    max-width:256px;
	height:100%;
    box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
	overflow-x:hidden
}

#language {font: 600 21px/1em roboto,noto sans cjk tc;width:80%}

#menu {
	margin:8px 0;
	padding:0;
}

#menubar > div,#menu > * {
	background:none;
	border:none;
	color:#fff!important;
}

#menu > * {
    margin:1px 0;
	padding:7px 24px;
}

.listhead {
	width:100%;
	margin:0;
}

.listhead:hover {
	background-color:#ff4081!important;  
	box-shadow:0 0 2px rgba(0,0,0,.12),0 2px 2px rgba(0,0,0,.24) ;
}

#menubar a {
	color:#fcfcfc!important
}
.menuitem *,.menuitem {
	font-weight:400!important
}

#content {
    background:none;
    position:relative;
    top:72px;
    padding: 8px 32px;
    height: calc(100% - 96px);
}

#titleline + table {
    margin: -8px 0 -24px;
    overflow: visible;
    text-align: center;
    height: 84px;
}

#content table img,select[name="method"] {
	-webkit-filter:brightness(.976) ;
    max-height: 100%;
}

td {
    vertical-align:bottom!important;
    padding: 0 4px;
}

#content a.noulgrey {border:none;}

input {
	border:none;
    font:inherit;
}

input[type="submit"] {
	text-transform:uppercase;
	font-weight:500;
    font-size:15px;
    color:#ff4081;
	background:none;
	border-radius:6px;
	box-sizing:border-box;
	margin:4px;
	padding:0 1em;
	white-space:nowrap;
	transition:.2s;
	height:28px;
}

#content input[type="submit"]:hover {
	background:rgba(153,153,153,.2)!important
}

#menubar input[type="submit"] {
	background:#ff4081!important;
	box-shadow:0 0 2px rgba(0,0,0,.12),0 1px 2px rgba(0,0,0,.24);
	color:#fff
}

input[type="text"] {
	background:#fff!important;
	border:1px solid rgba(0,0,0,0.26);
    padding: 1px 8px;
    margin: 1px 0;
	color:#222;
	box-shadow:0 1px 1px rgba(0,0,0,0.2) inset
}

input[type="text"]:focus {
	outline:0;
	border-color:#0097a7;
	box-shadow:0 1px 2px rgba(0,0,0,0.3) inset
}

table * {
	background-color:transparent!important
}

th {
	font-weight:bolder
}

select,select:focus {
	border:none;
	outline:0;
	padding:.5em
}

[style*="url("] {
	background-blend-mode:multiply;
	background-color:#f9f9f9;
}

@font-face {
	font-family:'Material Icons';
	font-style:normal;
	font-weight:400;
	src:local('Material Icons'),local(MaterialIcons-Regular),url(https://fonts.gstatic.com/s/materialicons/v7/2fcrYFNaTjcS6g4U3t-Y5ZjZjT5FdEJ140U2DJYC3mY.woff2) format("woff2")
}

.material-icons {
	font-family:'Material Icons';
	font-weight:400;
	font-style:normal;
	font-size:24px;
	line-height:1;
	letter-spacing:normal;
	text-transform:none;
	display:inline-block;
	word-wrap:normal;
	-webkit-font-feature-settings:liga;
	-webkit-font-smoothing:antialiased
}

form {
	position:relative;
	min-width:160px;
	max-width:315px
}

.searchbox {
    width: calc(100% - 24px);
}

form[action="dictionary.pl"]:not(#searchform)::before, #searchform:last-child::before {
    content: "";
    font-family: 'Material Icons';
    position: absolute;
    top: 6px;
    right: 12px;
    font-size: 1.25em;
    color: rgba(0,0,0,0.5);
    line-height: 1.25em;
}

form#searchform::before {
	content:"";
	font-family:'Material Icons';
	position:absolute;
	top:12px;
	right:38px;
	font-size:1.25em;
	color:rgba(0,0,0,0.5);
	line-height:1.25em
}


a[onclick="if(getActiveStyleSheet()=='Compact') setActiveStyleSheet(''); else  setActiveStyleSheet('Compact'); return false;"] img,#home img {display:none!important}

a[onclick="if(getActiveStyleSheet()=='Compact') setActiveStyleSheet(''); else  setActiveStyleSheet('Compact'); return false;"]{position:relative;margin:0 .35em 0 1em;}
a[onclick="if(getActiveStyleSheet()=='Compact') setActiveStyleSheet(''); else  setActiveStyleSheet('Compact'); return false;"]:before{
    content:"";
    display:block;
    position:absolute;
    font-family:'Material Icons';
    top:.1em;
}



.noul:hover {
	font-family:Roboto,noto sans cjk sc light,HanaMinA,HanaMinB;
}




::selection {
	background-color:#00bcd4;
	color:#fff
}

*[class^="sprite"] {
    margin-right:.3em;
}

hr {
    height: 0px;
    border: none;
    border-top: 1px solid rgba(0,0,0,0.2);
}

div[style*="background-color: #EEEEEE;"],#source,.rhsbox,.optrhsbox {
	background:rgba(0,0,0,0.1);
	border:none!important;
	border-radius:3px
}

.noprint.opt {
    display: none;
}

}
