css:
@-moz-document domain("www.version2.dk") 
{
	body
	{
		font-family: Arial, Helvetica, sans-serif;
	}
	.wrap-top
	{
		display:none;
	}
	#mini-panel-comments_and_form
	{
		width:initial;
	}
	.banner,
	.panel-panel.panel-col-last.sidebar,
	.related-articles.top-three,
	#mini-panel-jobfinder_front,
	.threecol-pane.wp-list,
	#mini-panel-courses_pane,
	#mini-panel-threecol_panel_2,
	.comp-list
	{
		display:none;
	}
	.blog-page-list.container h1, .blogs-overview h1, .front-pane h1, .items-pane h1, .node-title-pane h1
	{
		font-size:25px;
	}
	.front-pane>.front-news>h2,
	.items-pane>.item-news>h2
	{
		font-size: 18px;
	}
	.node .n-teaser
	{
		font-size: 20px;
	}
	.byline
	{
		font-size: 15px;
	}
	custom-style1 a
	{
		color: rgba(120,129,0,1);
	}
	.custom-style1 a
	{
		color: rgba(120,129,0,1);
	}
	.article-link p>a, .node .body a, .comment-body a, .preview p a
	{
		color: rgba(130,139,0,1);
		text-decoration: underline;
		border-bottom: none;
	}
	.short-list h1::before
	{
		height:initial;
		margin:initial;
	}
	h2
	{
		font-size: 17px;
	}
	h3
	{
		font-size: 16px;
	}
	#mini-panel-comments_and_form article
	{
		padding: 10px 20px 5px;
		background: -moz-linear-gradient(top, #e5e5e5 0%, #fefefe 100%);
    	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e5e5e5), color-stop(100%,#fefefe));							
	}
}
