css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.deezer.com") {
  @keyframes threedfx {
    0% {transform: perspective(600px) rotateX(5deg);}
    25% {transform: perspective(600px) rotateX(0deg);}
    50% {transform: perspective(600px) rotateX(-5deg);}
    75% {transform: perspective(600px) rotateX(0deg);}
    100% {transform: perspective(600px) rotateX(4.8deg);}
  }
  
  @keyframes ppfadeOut {
    from {opacity: 1;}
    to {opacity: 0.4;}
  }
  
  @keyframes ppfadeIn {
    from {opacity: 0.4;}
    to {opacity: 1;}
  }
  
  @keyframes headerIntro {
    0% {top: -85px; transform: perspective(1000px) rotateX(90deg);}
    50% {top: -85px; transform: perspective(1000px) rotateX(90deg);}
    100% {top: 0px; transform: perspective(0px) rotateX(0deg);}
  }
  
  @keyframes divIntro {
    0% {top: 0px; height: 100%}
    50% {top: 0px; height: 100%}
    100% {top: 85px; height: calc(100% - 85px);}
  }
  
  body {
    background-color: black !important;
  }
  
  .page-sidebar {
    width: 100%;
    height: 85px !important;
    z-index: 50 !important;
    
    animation-name: headerIntro;
    animation-duration: 4s;
  }
  
  .panel-overlay {
    opacity: 0.9 !important;
    z-index: 1 !important;
  }
  
  .page-main {
    width: 100%;
    height: calc(100% - 85px);
    top: 85px;
    left: 0% !important;
    margin: 0px;
    z-index: 0 !important;
    
    animation-name: divIntro;
    animation-duration: 4s;
  }
  
  .search-control {
    float: left;
    width: calc(100% - 480px) !important;
    position: relative !important;
    margin: 0px !important;
    margin-left: 1% !important;
    z-index: 50 !important;
  }
  
  .panel-wrapper {
    height: 80%;
    width: 50% !important;
    margin-left: -25% !important;
    left: 50% !important;
    top: calc(2% + 85px) !important;
    position: fixed !important;
    z-index: 2;
    overflow: hidden;
    
    animation-name: threedfx;
    animation-duration: 30s;
    animation-iteration-count: infinite;
  }
  
  .player {
    height: 100% !important;
    left: calc(100% - 300px) !important;;
    width: 300px;
    position: absolute;
    z-index: 0 !important;
  }
  
  .player-controls {
    opacity: 1;
  }
  
  #player-cover {
    position: absolute;
    width: 100%;
    margin: 0%;
    opacity: 0;
    z-index: -1 !important;
    height: 1px !important;
  }
  
  .logo-deezer {
    float: left;
    width: 120px !important;
    margin-top: 5px;
    margin-left: 85px;
  }
  
  .profile-picture {
    height: 25px !important;
    width: 25px !important;
    position: fixed !important;
    margin-top: 30px !important;
    top: 0% !important;
    left: 50px !important;
    z-index: 100 !important;
    opacity: 0.4 !important;
    
    animation-name: ppfadeOut;
    animation-duration: 1s;
  }
  
  .profile-picture:hover {
    animation-name: ppfadeIn;
    animation-duration: 0.5s;
    
    opacity: 1 !important;
  }
  
  .icon-settings {
    width: 15px;
    height: 15px;
    position: fixed !important;
    top: 7.5px !important;
    margin-top: 18px !important;
    left: 0% !important;
    
    z-index: 100 !important;
  }
  
  .player-overlay, .player-cover {
    height: 1px !important;
    position: fixed;
    z-index: -1;
  }
  
  .player-progress {
    position: fixed !important;
    top: 0px !important;
    margin-top: 85px !important;
    width: 100% !important;
    left: 0px !important;
    margin: 0px !important;
    height: 3px !important;
    z-index: 5 !important;
  }
  
  .progress-seek, .progress-seek-overlay {
    top: 0px;
    margin-top: 83px;
    height: 2px !important;
    background-color: white !important;
    z-index: 1 !important;
  }
  
  .container, .page_feed {
    width: 98% !important;
    max-width: 98% !important;
    padding: 1% !important;
    margin: 0px !important;
  }
  .card, .card-release-album, .card-algo-playlist {
    float: left !important;
    margin: 1% !important;
    width: 48% !important;
    min-width: 800px !important;
    height: 388px !important;
  }
  
  .divider {
    width: 100% !important;
    margin: 0px !important;
    float: left !important;
  }
}
