css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("actualidad.rt.com") {
   .section .cell, .section-visible .cell,.b-wrapper{
     background:#303030 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC) repeat!important;
   }
  .arcticle-content p, .arcticle-content ul, .arcticle-content ol,.poll-results h4{
    color: #BCBCBC;
  }
  .arcticle-heading {
    color: #EFEFEF;
  }
  .arcticle-content h3, .arcticle-content h4, .arcticle-content h5, .arcticle-content h6, .arcticle-content h1, .arcticle-content h2,.arcticle-summary,.arcticle-related h2,.article-comments .header,.most-populars h2,.poll .header,.item-2 span{
    color: #E4E4E4;
  }
  .article-comments .content,.last-brief-news,.most-populars,.poll{
    background: transparent;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.55);
  }
  .cc .mc-comment-body{
    color: #B4B4B4 !important;
  }
  .cc .mc-theme-standard .mc-comment-head{
    box-shadow:  0px 1px rgba(255, 255, 255, 0.1) inset, 0px 1px 3px rgba(0, 0, 0, 0.5)!important;
    background: linear-gradient(rgb(50,50,50), rgb(40,40,40))!important;
    color: rgb(166,166,166)!important;
    border:none!important;
    border-radius: 0px !important;
  }
  a{
    color:rgb(0,173,238);
  }
  a:hover{
    text-shadow: 0px 0px 5px rgba(0,173,238,.75);
    text-decoration: none;
  }
  .arcticle .themes-links{
    background-color: rgba(66, 66, 66, 0.33);
  }
  .arcticle .themes-links li,.last-brief-news .list-section,.most-populars .list-element,.poll-wrapper{
    background: transparent;
  }
  .last-brief-news .list-element,.most-populars .list-element {
    border-bottom: 1px solid #363636;
  }
  .last-brief-news .list-element:hover,.most-populars .list-element:hover{
    background: transparent -moz-linear-gradient(center top , rgba(51, 51, 51, 0.5) 0%, rgba(24, 24, 24, 0.45) 100%) repeat scroll 0% 0%!important;
  }
  .last-brief-news .list-element:hover a,.most-populars .list-element:hover a{
    color:beige;
  }
  .last-brief-news .news_link ,.most-populars .news_link{

    color: #939393;
  }
  .subscribe{
    display: none;
  }
  .b-header .top-panel {
    background-color: #202020;}
  .poll-wrapper{
    border: none;
  }
  .most-populars .b-tabs li.active{
    background: transparent;
    border-color: rgb(119, 188, 31) rgba(21, 21, 21, 0) rgb(63, 63, 63);
    color: #F5F5F5;
    font-weight: bold;
  }
  
}
