css:
@-moz-document domain("dou.ua") {
.g-page, .b-footer-slider, .title.__separated, .b-vacancy-page .b-vacancy, .table, #container, body, .b-login-dialog .border {
    background:#222;
}

.b-footer{
    background:#222;
    border-top:2px solid #444;
}

.b-typo, .b-user-info, h1, .page-profile .l-content-wrap .head .descr, .page-profile .b-author .name a, .page-profile .b-author .prof, b, dd, div, dl, dt, em, form, h1, h2, h3, h4, h5, h6, i, label, p, span, strong, sub, sup, tbody, tfoot, thead, tr, u, .b-footer a, .b-adv-events .adv-event-block .link, .b-index-columnisty .author a, .page-index .b-block .title h3 a, .b-index-discussions .meta a, .b-compinfo .info .l-n a, .b-recent-searches li.cat ul.vs li.vy a.comp, .b-recent-searches li.cat .cat-link, .b-vacancies-page .l-vacancy .vacancy .title a.company, .b-sub-head-n ul.sub li.sel a, .b-sub-head-n ul.sub li a.smi, .b-head li a.sel span, .page-events .info .date, .b-similar .item .author a, .b-content-menu li a, .b-sidebar-comments .b-sidebar-comment .comment, .b-companies-top25 table.mr td a, .b-typo .premium td, .b-typo .premium th, .b-last-comments .item .link .img a, .b-form .input-label, .b-login-dialog .b-content-menu li a, .comments-head .comment-subscribe span, .comments-head .navigation-comment, .b-similar .item .title a.link, .b-companies-ranks table.mr td a, .b-companies-ranks table.mr th a{
    color:#989898;
}

.b-similar{
    background:#222;
    border:2px solid #989898;
}

.date, .pageviews, .b-post-info .author .name a, .g-approved-user .b-post-author .avatar, .announce, .fixed-menu, .b-dou-vacancies .vacancies-list li .company, h3, h4 {
    color:#989898;
}

.b-comments .comment .sup-users a{
    color:#9E9E9E;
}

.b-dou-vacancies{
    background:#222;
    border-color:#737373;
}
.comments-head.__fixed .fixed-menu{
        background-image: linear-gradient(to bottom,#ADADAD,#0A0A0A);
}

.b-typo blockquote{
    background:#444;
}

.b-index-discussions .comment{
    color: #989898;
    border: 1px solid #888;
    background: #222;
    background-image: linear-gradient(to top,#111,#333);
}

.b-index-discussions .comment:hover{
    background-image: linear-gradient(to top,#333,#111);
}

.b-balls .black-ball{
    background:green;
}

 .b-sub-head-n .wrap, .b-head ul li a.sel {
    background:#333
}

.b-adv-events .adv-event-block .link .img:after {
    background-image:linear-gradient(to right, white, white, transparent);
}

.b-adv-events .adv-event-block .link .img:before {
    background-image:linear-gradient(to left, white, white, transparent);
}

.b-adv-events .adv-event-block .link .img:before{
    margin-right:-9px;
}

.b-adv-events .adv-event-block .link .img:after{
    margin-left:-9px;
}

.b-compinfo, .b-jobs-search{
    background-image:linear-gradient(to bottom,#111,#333)
}

.b-sub-head-n ul.sub li.sel{
    background-image:linear-gradient(to bottom,#333,#111)
}

.b-jobs-search{
    border-top:1px solid #888;
}

.b-sub-head-n ul.sub li.sel{
    border:1px solid #888;
    border-bottom:0;
}

.b-sub-head-n ul.sub li a.smi, .b-head li a.sel span{
    border-bottom:1px solid #777;
}

.b-content-menu .sel .wrap, .b-content-menu .sel .wrap b{
    background-image:none;
}

.b-content-menu .sel .wrap:hover, .b-content-menu .sel .wrap b:hover{
    background-image:none;
}

.b-content-menu .sel{
    background-image:linear-gradient(to bottom, #999, green);
    border-radius:5px;
    
    font-size:15px;
}

.b-content-menu .sel a span{
    color:#E5E2D7;
}

.b-content-menu .sel{
    height:26px;
}

.b-content-menu .sel:hover{
    background-image:linear-gradient(to bottom, #444, green);
}

.g-right-shadowed{
    background-image:linear-gradient(to right, #4D4D4D, #222, #4D4D4D);
}

.b-sidebar-comments .b-sidebar-comment .comment{
    background-image:linear-gradient(to top,#333,#111);
}

.b-sidebar-comments .b-sidebar-comment .more:before{
    background-image:linear-gradient(45deg, #222, #222, #ebe2ce);
    top:0px;
}

.b-companies-top25 .sticky-header .sticky-header-inner{
    background-image:linear-gradient(to top, #555, #222);
}

.b-companies-top25 table.mr tbody tr:hover{
    background:#111;
    border-radius:5px;
    border:1px solid #989898;
}

.b-balls ul li {
    background-image: url(http://5.firepic.org/5/images/2015-08/15/3u46yci7eq7o.png);
    background-color:transparent;
}

.b-typo .premium strong{
    color:#B71515;
}

a{
    color:#108798;
}

.b-most-interesting .b-switcher .bg-l{
    background-image:linear-gradient(to right,#222,transparent);
}

.b-most-interesting .b-switcher .bg-r{
    background-image:linear-gradient(to left,#222,transparent);
}

.b-most-interesting .b-switcher:after{
    background-image:linear-gradient(to top, #222, #222, transparent);
}

.b-login-dialog .alt-login-method{
    background-image:linear-gradient(to top, #666, #111);
}

.b-comments .comment .support{
    background-image:url(http://2.firepic.org/2/images/2015-08/12/z6xlftzga5qk.png);
}

.b-content-menu .sel sub{
    color:#A02223 ;
}

.page-profile .l-content-wrap .head h1 .va{
 background:url(http://6.firepic.org/6/images/2015-08/12/0n0z5ugropk8.png) no-repeat;
}

.b-last-comments .item .comment-item .text blockquote, .b-comments .comment .b-typo blockquote{
    background:#333;
}

.page-index .b-sponsors .img{
    border:3px solid #999;
    border-radius:5px;
}

.b-content-menu .sel .wrap span{
    color:white;
}

.big-button:hover{
    border-color:#444;
}

.big-button{
    background-image:linear-gradient(to top, green, #999);
    border:0;
    box-shadow:0 0 transparent;
}

.big-button:hover{
    background-image:linear-gradient(to top, green, #444);
}

.big-button .unchecked-text, .big-button.disabled{
    color:white;
}

.pageviews{
    background-image:url(http://5.firepic.org/5/images/2015-08/15/sedp57aj5l4j.png);
}

/*.social-likes__widget{
    background-image:linear-gradient(to top, #2EEA30 ,#17BA18, #111 );
}*/

/*tr{
    background:#999;
}*/

tr strong{
    color:black;
}

.b-companies-ranks .sticky-header .sticky-header-inner{
    background-image:linear-gradient(to top, #555, #222);
}

.b-companies-ranks table.mr .no-cat{
    background:#333;
}

.b-companies-ranks table.mr tr:hover{
    background:#111;
    border-radius:5px;
    border:1px solid #989898;
}

.b-companies-ranks table.mr thead tr:hover, .b-companies-ranks table.mr tr.cat:hover{
    border:0;
}
}
