css:
@-moz-document domain(youtube.com) {
  .html5-video-player:not(.ytp-big-mode).ytp-chrome-bottom {
      transform: translateY(5px)!important;
      height: 40px;
  }
  .ytp-big-mode .ytp-chrome-bottom {
      height: 65px;
  }
  .ytp-chrome-bottom::before {
      content: '';
      bottom: 0px;
      top: -1px;
      left: -12px;
      right: -12px;
      position: absolute;
      z-index: -1000;
  }
  .ytp-chrome-controls {
      height: 36px!important;
      line-height: 36px!important;
  }
  .ytp-chrome-controls .ytp-button:not(.ytp-play-button):not(.ytp-watch-later-button) {
      width: 36px!important;
  }
  .ytp-play-button {
      width: 46px!important;
  }
  .ytp-progress-bar-container:not(.ytp-pulling) {
      height: 5px!important;
  }
  .ytp-big-mode .ytp-chrome-bottom {
      transform: translateY(24px)!important;
  }
  .html5-video-container {
      height: 100%!important;
  }
  .ytp-progress-bar-container {
      top: 0px !important;
  }
  .ytp-cards-button {
      top: 0!important
  }
  .ytp-big-mode .ytp-progress-bar-container {
      transform: translateY(-1px)!important;
  }
  .ytp-settings-button.ytp-hd-quality-badge::after,
  .ytp-settings-button.ytp-4k-quality-badge::after,
  .ytp-settings-button.ytp-5k-quality-badge::after,
  .ytp-settings-button.ytp-8k-quality-badge::after {
      content: ''!important;
      position: absolute!important;
      top: 6px!important;
      right: 4px!important;
      height: 9px!important;
      width: 13px!important;
      background-color: #f12b24!important;
      border-radius: 1px!important;
      line-height: normal!important;
      background-image: url('data:image/svg+xml;base64,')!important;
      padding: 0!important;
  }
  .ytp-player-content,
  .ytp-subtitles-player-content {
      bottom: 49px!important;
  }
  .ytp-big-mode .ytp-chrome-top .ytp-title {
      margin-top: 5px;
      font-size: 110%;
  }
  .ytp-big-mode .ytp-chrome-top .ytp-button {
      width: 36px !important;
  }
  .ytp-big-mode .ytp-chrome-bottom .ytp-time-display {
      font-size: 70%;
  }
}
