css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.furaffinity.net") {

body {
background-color: #800;
}
.addpad {
background-color: #
}
.logo {
background-color: #800;
}
.cat {
background-color: #F00;
border: 2px solid #800;
}
table.maintable {
background-color: #F00;
}
.innertable {
background-color: #800;
}
.alt1{
background-color: #800;
}
.hovertable{
	background-color: #6A7283;
}
.hovertable_head{
	background-color: #F00;
	color: #fff;
	border: 2px solid #800;
}
.hovertable_body{
	background-color: #800;
	color: #000;
}
.thumbnail{
	vertical-align: middle;
	border: 1px solid #800;
	padding: 2px;
}
.lead{
	border-top: 4px solid #b00;
	border-bottom: 4px solid #b00;
	border-left: 2px solid #b00;
	border-right: 2px solid #b00;
	color: #fff;
}
.alt2 {
	background-color: #800;
}
.tab{
	margin-left: 5px;
}
.tab u{
	background-color: #b00;
	border-top: 1px solid #f00;
	border-left: 1px solid #f00;
	border-right: 1px solid #f00;
	color: #fff;
	font-weight: bold;
	text-decoration: none;
	padding: 8px 4px 4px 4px;
}
.tab a{
	background-color: #800;
	border-top: 1px solid #f00;
	border-left: 1px solid #f00;
	border-right: 1px solid #f00;
	padding: 4px;
}
.tab a, .tab a:link, .tab a:visited, .tab a:active{
	color: #fff;
}
.tab a:hover{
	color: #000;
	text-decoration: none;
	padding-top: 8px;
}
.ldot{
	border-left: 1px dotted #f00;
	padding-left: 2px;
}
.rdot{
	border-right: 1px dotted #f00;
	padding-right: 2px;
}
.bdot{
	border-bottom: 1px dotted #f00;
	padding-bottom: 2px;
}
.tdot{
	border-top: 1px dotted #f00;
	padding-top: 2px;
}
.container-comment{
	text-align: left;
	border: 1px solid #f00;
	margin-top: 12px;
	border-collapse: collapse;
}
.container-comment TD,
.container-comment TH{
	vertical-align: top;
	padding: 4px;
}
.container-comment .icon{
	width: 108px;
	height: 108px;
	text-align: center;
	vertical-align: top;
	padding: 4px 0px;
}
.container-comment TH{
	background-color: #f00;
}
.container-comment TD.cat,
.container-comment TH.lead{
	text-align: right;
	border: 0px none;
}
.container-comment TH.lead{
	height: 16px;
	line-height: 16px;
	padding: 0;
}
.container-comment TH.lead H4,
.container-comment TD.cat A{
	margin: 0 10px 0 0;
	padding: 0;
}
.container-comment TH.lead H4{
	font-weight: normal;
	font-size: 7.5pt;
	font-family: Verdana;
}
.container-comment TD.cat A{
	background-color: #f00;
	padding: 1px 7px;
}
.container-comment TD.cat{
	height: 0px;
	line-height: 0px;
	padding: 0;
	border: 0;
	background-color: #0f0;
}
.container-comment .reply-link{
	position: relative;
	bottom: 7px;
	right: -10px;
}
.menu{
	text-align: left;
	margin: 0;
	padding: 0px;
}
.menu ul{
	text-align: left;
	margin: 0;
	padding: 0px;
}
.menu li{
	text-align: left;
	margin: 0;
	padding: 0;
	float: left;
	display: block;
	cursor: pointer;
}
.menu li ul{
	text-align: left;
	margin: 0;
	padding: 0;
	display: none;
	position: absolute;
}
.menu li ul li{
	float: none;
	text-align: left;
	border: 1px dotted #f00;
	background-color: #b00;
	cursor: pointer;
	padding: 3px;
	width: 200px;
}
.menu li ul li:hover{
	float: none;
	text-align: left;
	border: 1px dotted #800;
	background-color: #f00;
	color: #000;
	padding: 3px;
	cursor: pointer;
}
.menu li ul li A:LINK{
	text-decoration: none;
	color: #CFCFCF;
}
.menu li ul li A:VISITED{
	text-decoration: none;
	color: #CFCFCF;
}
.menu li ul li A:HOVER{
	color: #dcdcdc;
	text-decoration: none;
}
.menu li:hover ul{
	display: block;
}
div#messagecenter-submissions table.checked{
	background-color: #f00;
}
div#messagecenter-submissions h3.date-divider{
	border-bottom: 1px solid #f00;
}

div#messagecenter-other ul.message-stream table.checked{
	background-color: #6A7283;
}
div#messagecenter-other ul.message-stream li.checked{
	background-color: #f00;
}
div#messagecenter-other ul.message-stream li.hover{
	background-color: #b00;
}
div#messagecenter-other ul.message-stream li em{
	border-bottom: 2px dotted #f00;
}
div#messagecenter-other #messages-watches ul.message-stream li table td.avatar img{
	border: 1px solid #f00;
}
div#messagecenter-other ul.message-stream li.section-controls,
div#messagecenter-other #messages-watches ul.message-stream li.section-controls{
	clear: both;
	width: auto;
	height: auto;
	text-align: left;
	float: none;
	border-bottom: 1px solid #f00;
	margin-top: 10px;
	padding-left: 0;
	cursor: default;
}

}
