css:
/* i really want this to be global */

html
{
    background-color:#282c34;
}

.dash-project-avatar 
{
    display: none;
}

a
{
    text-decoration: none;
    color: #9BAAC9;
}

.nav-pills > li > a
{
    background-color:#2c3139;
    color: #ffffff;
}

.project-side .panel .nav-pills a {
 color: #ffffff;   
}

div.project-identicon-holder{
 display: none;   
}

.ui_mars .page-with-sidebar .sidebar-wrapper,
header.navbar-gitlab .container,
.well-list li:last-child.bottom,
.panel-default>.panel-heading 
{
    background-color: #323741
}


li.project-row:hover,
body > div > div.content-wrapper > div > div > div.clearfix > div > aside > div.panel.panel-default > ul > li > a:hover 
{
    background-color: #323741;
    transition: all 0.25s;
    -webkit-transition: all 0.25s;
    -moz-transition: all 0.25s;
}

.issues-details-filters,
.well
{
    background-color: #323741;
}

 
li.issue.closed > div.issue-title > span > a {
 text-decoration: line-through;
}

.issue.closed {
    background-color: #373d47; 
    border-color: #000000;
}

div.panel > ul > li.issue:hover,
ul.well-list issues-list > li:hover
{
    background-color:#585d65;
    transition: all 0.25s;
    -webkit-transition: all 0.25s;
    -moz-transition: all 0.25s;
}

.label.label-gray,
.label.color-label
{
    background-color: #525354 !important;
    color: #FFFFFF !important;
}

.issue.today
{
    background-color: #3E495A;
    border-color: #000000;
}

.nav-sidebar > li:hover > a > span{
    color: #fff;
    transition: all 0.25s;
    -webkit-transition: all 0.25s;
    -moz-transition: all 0.25s;
}

.project-side .panel .panel-heading
{
    color: #FFFFFF;
}

.btn,
.btn-sm,
.project-home-panel .project-repo-buttons .btn,
.timeline:before,
.nav-tabs > li.active > a,
.event_filter li a,
header.navbar-gitlab .container .nav > li > a
{
    background-color: #000000;   
}

.event_filter li a 
{
    background: #000000;   
}

.btn .btn-close
{
    color:#FFFFFF;
    background-color: #582220;
    border-color: #582220;
}

/* Color Begins */

i,
span,
header .header-content .title a,
body > header > div > div.header-content > h1 > span > a,
body > header > div > div.header-content > h1 > a,
li.commit .commit-row-title .commit-row-message
{
    color: #FFFFFF;
}

.nav-pills > li:hover > a{
 color: #000000 !important;
}

.btn,
.btn-sm{
 color: #FFFFFF;
 border-color: #000000;
}

body
{
 color: #ADADAD;   
}

#s2id_issue_milestone_id > a > span,
#s2id_issue_assignee_id > a > span{
    color: #000000;
}

.well-list li .row_title,
a 
{
    color: #8593AD;
}

.event-item .event-body 
{
    color: #919191;
}

li > a:hover > span,
.event_filter li a:hover > i,
a:hover > i
{
    transition: all 0.25s;
    -webkit-transition: all 0.25s;
    -moz-transition: all 0.25s;
    color: #000000;
}

/* Color Ends */

header .header-logo a img
{
    display: none;   
}

header .header-logo a h3 
{
    width: 100%;
    text-align: center;
    margin-left: 0px;
}

.well,
.panel
{	
    border: 1px solid #000000;
    background-color: #373d47;
}

.light-well, .description-block,
.breadcrumb.repo-breadcrumb,
.content-wrapper 
{
    /*background-color: #36393f;*/
    background-color: rgb(45, 45, 45)
}

.progress
{
    background-color: #474E58;
}

.progress-bar-success
{
    background-color: #253240;
}

.event-item .event-title,
li.milestone h4,
strong,
.project-row .project-name,
.issues-list .issue .issue-title 
{
    font-weight: normal;   
}

.issues-list .issue .issue-title a:hover
{
    text-decoration: none;   
}

/* Start Borders */

.well-list li,
.container:after,
.header-expanded .navbar .navbar-fixed-top .navbar-gitlab,
.event-item 
{
    border-bottom: 1px solid #000;   
}

header .header-content,
header .navbar-gitlab
{
    border-bottom: 0px;
}

blockquote
{
    border-left: 5px solid #000000;
}

hr
{
    border-top: 1px solid #000000;   
}

/* End Borders */
