css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("8tracks.com") {
  
  *, .mix_card .cover{
    image-rendering:optimizequality !important;
    text-rendering: optimizelegibility !important;
  }
  
  .sidebar{
    margin-top:-5px !important;
  }
  
  .mix_card .cover{
    max-height:200px !important;
    max-width:200px !important;
    left:14px !important;
  }
  
  .sidebar_mix .cover{
    max-height:100px !important;
    max-width:100px !important;
    left:10px !important;
    top:5px !important;
  }
  
  
  body{
    font-family: Avant Garde,Avantgarde,Century Gothic,CenturyGothic,AppleGothic,sans-serif !important;
    background: linear-gradient(#D5FFAC 2%, #E3A8A6 95%, #E3A8A6 100%) repeat scroll 0% 0% #E3A8A6 !important;
  }
  
  
  #thin_header, #header_content, #logged_in_dropdown, .recent-mixes, .artist_details_container{
    background-color:#E3A8A6 !important;
  }
  
  #explore_button, .tokenfield .token, #browse, #thin_header a:hover, #logged_in_topnav:hover, #logged_in_topnav.active{
    background-color:#c79593 !important;
  }
  
  a.tag, .fav_track, #mix_edit_details .mixedit, #mix_edit_details .tags_editor, .new_review {
    background: transparent !important;
    border: 1.5px solid #E3A8A6 !important;
    color: #E3A8A6 !important;
}
  
  .card{
    background: transparent !important;
    border: 2px solid #E3A8A6 !important;
    border-radius:1px !important;
  }
  
  .big_menu a.item, .big_menu .item,  .profile_menu .item.selected, #tracks_played .track::before{
    color:#E3A8A6 !important; 
  }
  
  .profile_menu .item, h6, .track .album, .track .year, .track .track_metadata .detail{
    color:#E3A8A6 !important;
}
  
  .big_menu a.selected{
    color:#ffbfbd !important;
    border-color:#ffbfbd !important;
  }
  
  .big_menu a.selected::after{
   border-bottom: 7px solid #ffbfbd !important;
  }
  
  .card .reason::after, #profile_bottom::after{
    border:none !important;
    background:transparent !important;
  }
  
  .background-blur-container{
    background:#D7EEAA !important;
  }
  .background-blur, .sidebar_ad_wrapper{
    display:none !important;
  }
  
  .big_menu::after, .card .reason, .profile_menu .item.selected::after{
   border-bottom: 3px solid #E3A8A6 !important; 
  }
  
  #profile_top .counters a:link, #profile_bottom, a:link, .track .player, #logged_in_dropdown .menu-item, .recent-mixes .item, .end_card, #user_byline .byline, #description_html, #now_playing .track_metadata .label, .track, .new_review textarea, .flatbutton, a.flatbutton, #now_playing .title_artist, #now_playing .track_metadata .detail{
    color: #fff !important;
    text-shadow: 0px 0px 1px #cccccc !important;
  }
  
  .profile_menu .item.selected, #play_area, .publish_options, #track_selection_header, #track_search_header, #selected_tracks li:nth-of-type(2n), #sm2-container{
    background:transparent !important;
  }
  
   #logged_in_dropdown, .recent-mixes{
     border: 2px solid #E3A8A6 !important;
     border-radius: 5px !important;
     box-shadow:none !important;
   }
  
  #logged_in_dropdown .menu-item:hover{
    background:#c79593 !important;
    color: #fff !important;
  }
  
  #player_progress_bar{
    background: rgba(193, 230, 156, 0.9) !important;
  }
  
  #player_progress_bar_meter{
    background: rgba(227, 168, 168, 0.90) !important;
  }
  
  #browse a.tag{
    color: #fff !important;
    border: 1px solid #fff !important;
  }
  
  #filters{
    max-height: 200px !important;
  }
  
  .ad_card, .preroll_container, #superheader_box, #sidebar_ad, .sidebar_ad{
    display:none !important;
  }
  
  hr, .comment_footer{
    border-color:#E3A8A6 !important;
  }
  
  .interactbox{
    background: rgba(115, 183, 19, 0.2) !important;
  }
  
  .turquoise_button, a.turquoise_button{
    background: #6CD4FF !important;
    border-color: #6CD4FF !important;
  }
 
  h6{
    color: #D5FFAC !important;
  }
  

}
