css:
@-moz-document domain(tomusss.univ-lyon1.fr) {

body{
background-color: #EEE !important;
}
.LastGrades .a_grade, .CellBox{
padding: 3px;
border-radius: 6px;
border: 1px solid rgb(205, 187, 187);
}

#cellbox_tip {
padding: 7px;
margin-top: -8px;
margin-left: -8px;
background-color: rgb(237, 237, 237);
border: solid 1px #CDCDCD;
box-shadow: 0 0 7px #CAC8C8;
border-radius: 8px;
opacity: 1;
}

#cellbox_tip span.displaygrey{
color: #333!important;
font-weight: bold;
}

.Display.DisplayPreamble.Preamble{
display: none;
}

.LastGrades {
border: solid 1px #ccc;
padding: 10px;
box-shadow: 0 0 5px #C5C5C5;
background-color: rgb(152, 236, 217);
}

.DisplayHorizontal hr {
display: none;
}
}
