css:
@-moz-document url-prefix("https://www.duolingo.com/") {

/* 

Created by Szczypioreg 

*/ 

/* Tlo glowne */
#app {
	background-color:#292929;
}
/* Tekst H1 - niebieski */
h1 {
    color: #1caff6;
}
/* Tlo podczas rozwiazywania tematow */
.slide-in {
	background-color: #191919;
}
/* Wiekszosc bialych tekstow */
body, h2, h4, h5, .level-text, .gray-dark, .skill-badge-small.locked .skill-badge-name, .skill-badge-small, .gray, .btn-faded.btn-green.btn-outline, #app.correct, .player-footer, #app.correct, .player-footer{
	color: #fff;
}
/* Tekst w zielonym przycisku */
.btn-yellow {
 	color: #000;   
}
/* Zablokowane ikony tematow - granatowe */
.skill-icon.locked {
	background: hsla(200, 80%, 37%, 0.5);
}
/* Podpisy pod inonami tematow - biale */
.skill-badge-small {
	color: #fff;   
}
/* Tlo tematow i lekcji */
main:not(.full-width) .page-main {
 	background: #191919;   
}
/* Tlo okienek po prawej - na gorze - szare */
.bg-white {
 background: #292929;  
}
/* Tlo okienek po prawej - na dole - szare */
.box-gray {
	background: #292929; 
}
/* Nie wiem co to, ale niech jest */
.inner > h2 {
	color: #00ff00;
}
/* Tekst w okienku do tlumaczenia */
.non-space, .token, .inner {
 	color: #000;   
}
/* Numery lekcji w oknie ich wyboru */
.list-lesson-item > .inner > h3 {
    color: #fff;
}
/* Zablokowana lekcja w oknie wyboru - szary (jak tlo) */
.list-lesson-item.locked > .inner {
	background: #292929;
}
/* Przycisk 'sprawdz' w lekcji - zielony */
.btn-green, .success {
	background: #81DA02 !important;   
}
/* Nowe slowo w lekcji - pomaranczowe */ 
.highlighted-new-word {
	color: #ff9d00 !important;   
}
/* Zla odpowiedz w lekcji > kolor ramki i czcionki w przyciskach 'zglos problem' i 'dyskusja' - czerwony, czarny */
.btn-faded.btn-red.btn-outline {
    border-color: #FF2500;
    color: #000000;   
}
/* Ilosc ukonczonych lekcji w menu wyboru */
.skill-progress-headline, .skill-progress-headline > span{
    color: #fff !important;   
}
/* Tekst 'cwicz tematy' */
#practice-subheader {
    color: #fff;
}
/* Nowe słowo - odmiana */ 
tbody {
 color: #000;  
}
/* dyskuja dotyczaca zdania w lekcji */ 
bdi > p {
 color: #fff; 
}
/* Tlo w dyskusji */ 
.modal-body {
  background: #3f3f3f;
}
/* Podpowiedź w lekcji typu: "w tym miejscu musi sie znaleźć to" */
.blame-text {
   color: #fff;
}
/* Poziom danego jezyka w polu dyskusji */
.user-flair > li {
    color: #fff;
}
/* Tekst po zdobyciu poziomu */ 
.lingot-bill > .lingot-bill-items> li {
    color: #000;
}
}
