css:
@-moz-document domain("tweetdeck.twitter.com") {
    
.app-columns-container {
    background-color: #37474F!important
}

.column-number {
    display: none!important
}

body {
    font-family: Arial Rounded MT Bold, Helvetica Rounded, Arial, sans-serif!important
}

* {
    text-decoration: none!important;
    border: 0!important;
    text-shadow: none!important;
    box-shadow: none!important
}

.js-item-launch a,
.prf-stats li+li a {
    border: 0!important
}

.app-header,
.app-navigator,
.app-title,
.column-header,
.column-nav-item {
    background-color: #263238!important;
    border: 0!important
}

strong {
    font-weight: 400!important
}

.column,
.js-detail-container,
.js-stream-item-content,
.mdl,
.prf-meta,
.scroll-conversation,
.tweet-detail-actions {
    border: 0;
    background-color: #202B30!important
}

.js-profile-columns,
.mdl-column-med {
    background-color: #12181B!important
}

.js-column-back,
.js-column-back:hover {
    color: #fff!important
}

.attach-compose-buttons .tweet-button,
.compose {
    background-color: #19252C!important
}

.btn-on-blue {
    background-color: #1F2E35!important
}

.btn-on-blue:hover {
    background-color: #021A27!important
}

.app-search-fake {
    color: #fff!important
}

.inline-reply {
    background-color: #1C282D!important
}

.prf-stats a strong {
    color: #fbfcfc!important
}

.social-proof-container {
    background-color: #181F23!important;
    color: #fbfcfc!important
}

.stream-item {
    border-bottom: 0!important
}

.column-drag-handle {
    position: absolute!important;
    top: 8px!important;
    left: 1px!important;
    margin-right: 10px!important;
    opacity: .4!important
}

.list-account .fullname {
    color: #fff!important
}

scrollbar {
    display: none!important
}

.mdl-column-med {
    border-radius: 0!important
}

.app-search-fake,
.app-search-input {
    background-color: #202B30!important
}

.js-list-container li a {
    margin: 0 12px!important;
    background-color: #202B30!important
}

.popover {
    background-color: #172025!important
}

.is-inverted-dark .accordion-header,
.is-inverted-dark .accordion-header:hover,
.is-inverted-dark .facet-type-content.is-active,
.is-inverted-dark .facet-type-engagement.is-active,
.js-search-in-popover .list-item {
    color: #fff!important
}

.list-item.is-selected,
.list-item:active,
.list-item:hover {
    background-color: #1F2B30!important
}

.list-item-button,
.quoted-tweet {
    background-color: #263238!important
}

.mdl-content .column-type-icon {
    display: none!important
}

.mdl-content .js-column-header {
    padding: 0!important
}

.column-head-title {
    color: #DAD7D7!important
}

.js-add-column-search-input {
    border-radius: 0!important;
    margin: 10px 8px!important;
    width: 95%!important;
    color: #fbfcfc!important;
    background-color: #202B30!important
}

.js-list-container li a:hover {
    background-color: #171E21!important
}

.txt-mute .js-tweet-text {
    color: #999!important
}

.js-tweet-text {
    color: rgba(255, 253, 253, .78)!important
}

.lst-group .selected {
    background: #12181B!important
}

.mdl-column-med {
    color: #fff!important
}

.is-inverted-dark .account-link {
    color: #F1EDED!important
}

::-webkit-scrollbar-thumb {
    border-radius: 0!important;
    background-color: #172025!important;
    transition: all .3s ease!important
}

::-webkit-scrollbar-thumb:hover {
    background-color: #0F1518!important
}

::-webkit-scrollbar-track {
    border-left: 1px solid #1E272C!important
}

.column-options,
.column-options .button-tray {
    background-color: #0F161A!important
}

.column-header-link {
    color: #E2E2E2!important
}

.is-options-open .column-settings-link {
    background-color: #202B30!important;
    color: #fff!important
}

.column-background-fill {
    background-color: #202B30!important
}

.mdl-content {
    border-radius: 0!important;
    background: 0 0!important;
    background-clip: padding-box
}

.account-inline:hover,
.account-link:hover,
a:hover {
    color: #4db2ec!important;
    text-decoration: none!important
}

.detail-view-inline,
.tweet-stats {
    border: 0!important
}

.mdl-header-title {
    padding-left: 26px!important;
    color: #fff!important;
    line-height: 17px!important
}

.js-trends-column{
	display: none !important;
}
    
}
