css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('bfmtv.com'), domain('01net.com') {
  
  	.pubbanner, #code-promo {
		display: none;
	}
	
	.container {
    width: 90%;
  }
	
	/*Navigation rubrique (01net>Actualités>Applis, Logiciels)*/
	div.hidden-xs.text-center.border-t-s.container.noshadow.main_containt {
		display: none !important;
	}
	
	/*sous menue*/
	div.main_containt.content_full_size.bg-color-1.noshadow.nav2.hidden-xs.border-b-s {
		display: none !important;
	}
	
	.navbar, .navbar-default {
    background: #14171a;
    color: whitesmoke;
  }

	/*Colone gauche - Bouton gauche (com + reseau social + imp)*/
	.modulx7 {
		display: none !important;
	}
	
	/*Pub cologne droite*/
	.art-vspacing {
		display: none !important;
	}
	
	/*Image pricipal article*/
	.art-spacing {
		padding: 0px 60px 15px;
	}
	
	/*Contenue article*/
	.col-xs-7, .col-xs-10, .col-md-7, .col-md-10 {
		width: 100% !important;
	}
	
	/*rubant avant commentaires*/
	div.rub.main_containt.noshadow.content_full_size.no-margin.bg-color-0 {
		display: none;
	}
	
	/*colone gauche commentaires invisible */
	div.col-md-1000-1.col-md-2 {
		display: none;
	}
	
	/*commentaires*/
	div.col-md-1000-7.art-spacing.col-xs-7, div.col-md-1000-11.art-spacing.col-xs-10 {
		width: 100%;
	}
	
	/*A lire aussi*/
	section.padding-bottomx2.blocx2.border-b-s {
		display: none
	}
	
	footer, #footerMainDiv, #footerDegrade {
		display: none;
	}
	
	/*dark mode*/
	
	/*background*/
	#wrap, .main_containt.bg-color-0 article, .bg-color-1 {
    background-color: #333;
		color: #fff;
	}
	
	/*title article*/
	.main_containt {
		background-color: #333;
		color: #fff;
	}
	
	/*puce rubrique active*/
	#site01net .nav-label .active::after {
		color: #333;
	}
	
	a {
    text-decoration: none;
    color: #32a9da !important;
  }

  a:hover {
    color: #86E2D5 !important;
    transition: color 0.2s linear 0s;
  }

  a:visited {
    color: #CC9393 !important;
  }
	
	.color-txt {
    color: whitesmoke !important;
  }
	
	/*Block poste autor*/
	
	.bg-color-0 {
    background-color: #14171A;
	}
	
	.border-b, .border-default-b {
    border-bottom: initial;
	}
	
	/*Formulaire commentaires*/
	.form-control {
		color: #fff;
		background-color: #333;
	}
	
	.white-popup {
		background-color: black;
	}

	/*bodure sous le formulaire de commentaires*/
	form.form-horizontal.blocx2.border-b-s.padding-bottomx2.post-comment-form {
		border-bottom: none;
	}
 
}
