css:
@-moz-document url-prefix("http://glebov.com.ua/") {
body{
    background-image:url(http://www.hdoboi.org/large/201307/5611.jpg);
    background-size:100%;
    background-attachment:fixed;
}

#head, .content, .row_box_left, .row_box_for4{
    background-color:#F3FCF3;
    border:1px solid #CDF0CC;
    border-radius:3px;
}

.row_box{
    background:transparent;
}

#head{
    background-image:url(http://2.firepic.org/2/images/2015-08/12/zynuaeqk1scm.png);
}

.row_box_for4 h3{
    color:#EAEBF6;
}

#head_L1, p{
    font-family:Arial;
}

.news{
    background:transparent;
}

a img:hover{
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
    transition:0.5s;
}

#head_contact{
    transition:0.5s;
    margin-bottom:15px;
    border-radius:3px;
    margin-left:15px;
    padding:3px;
}

#head_contact:hover{
    transition:0.5s;
    background:rgba(163,163,163, 0.3);
}

#menu{
    margin-left:16px;
}

#head_L1, #head_L2{
    margin:15px;
}

.menux_l1 span, .menux span, .row_box h2{
    transition:0.3s;
    background:#F3FCF3 !important;
    padding:10px;
    border-bottom:1px solid green;
    border-radius:10px;
}

.menux_l2 span, .menux span{
    color:#535897;
    padding:5px;
    border-bottom:1px solid blue;
}

.menux_l2 span:hover, .menux span:hover, .menux a:hover span{
    transition:0.3s;
    background-image:linear-gradient(to bottom, white, #EBEBEB) !important;
    text-decoration:none;
    box-shadow:0 0 10px blue !important;
}

#menu a{
    transition:0.5s;
    color:#3D648A;
    border:2px solid #3D648A;
    border-radius:3px;
    background:#fff;
    font-size:15px;
    font-weight:bold;
}

#menu a:hover{
    transition:0.5s;
    background:#3D648A;
    color:white;
}

#menu a.current{
    background:#3D648A;
    color:white;
}

#menu{
    padding-top:12px;
    padding-bottom:12px;
}

.content p{
    margin-left:30px;
    margin-right:30px;
}

.content h1{
    margin-top:20px;
    margin-bottom:5px;
}

.menux_l1 span:hover, .row_box a:hover h2{
    background-image:linear-gradient(to bottom, white, #EBEBEB) !important;
    transition:0.3s;
    text-decoration:none;
    box-shadow:0 0 10px green;
}

.news_left_head, .row_box_for4 h3{
    padding:5px;
    border-radius:4px;
    box-shadow:0 0 5px blue;
    margin:5px 7px;
}

.news_left_intro, .content_box_for4{
    padding:10px 10px 5px 10px;
}

.news_left_body  a{
    padding-left:10px;
}

.row_box_for4{
    height:245px;
    margin:1px;
}

.row_box_for4 h3{
    margin:10px 10px;
}

.row_box_for4:hover{
    transition:0.7s;
    background:linear-gradient(to top, #F3FCF3, #BBB);
}

.date_box_for4{
    margin-right:15px;
}

div.row_box_for4:nth-child(4n+1), .row_box_left{
    margin-left:0px;
}

.row_box a:hover{
    background:transparent;
}

.news_lenta_text:hover{
    background:#F3FCF3;
    box-shadow:0 0 10px blue;
    border-radius:5px;
}
}
