css:
@-moz-document domain("www.version2.dk") {
body, .footer
{
	font-family: Arial, Helvetica, sans-serif;
	background-color:#404040;
	color:white;
}
div.EmbeddedTweet-tweet
{
	color:black !important;
}
#page
{
	background-color:transparent;
	color:white;
}
.node .body li 
{
    color: #c8d100;
}
.wrap-top
{
	display:none;
}
#mini-panel-comments_and_form
{
	width:initial;
    max-width:1060px;
}
.header, .footer
{
	background: #303030;
}

.twitter,
.facebook,
.linkedin,
.googleplus,
.banner,
.panel-panel.panel-col-last.sidebar,
.related-articles.top-three,
#mini-panel-jobfinder_front,
.threecol-pane.wp-list,
#mini-panel-courses_pane,
#mini-panel-threecol_panel_2,
.comp-list,
.article-link,
#mini-panel-related_branch_news,
.social-tools-pane~.social-tools-pane,
.v2-advertorial.ad-white
{
	display:none;
}
.blog-page-list.container h1, .blogs-overview h1, .front-pane h1, .items-pane h1, .node-title-pane h1
{
	font-size:25px;
}
.front-pane>.front-news>h2,
.items-pane>.item-news>h2
{
	font-size: 18px;
}
.node .n-teaser
{
	font-size: 20px;
}

custom-style1 a
{
	/*color: rgba(120,129,0,1);*/
}
.custom-style1 a
{
	/*color: rgba(120,129,0,1);*/
}
.comment p a
{
	color:rgba(100,109,0,1);
}
.article-link p>a, .node .body a, .comment-body a, .preview p a
{
	color: #c8d100;
	text-decoration: underline;
	border-bottom: none;
}
.short-list h1::before
{
	height:initial;
	margin:initial;
}
h2
{
	font-size: 17px;
}
h3
{
	font-size: 16px;
}
#mini-panel-comments_and_form article
{
	padding: 10px 20px 5px;
	background: -moz-linear-gradient(top, #e5e5e5 0%, #fefefe 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e5e5e5), color-stop(100%,#fefefe));							
}
.node .body
{
	color: #c8d100;
	font-style: italic;
}
section.body
{
	text-align: justify;
	border-top: 1px solid #c8d100;
	padding-top: 20px;
	margin-bottom:15px;
}
.body p, 
.body h2, 
.body h3, 
.body h4
{
    color:white;
    font-style:normal;
}

.body h2, 
.body h3, 
.body h4
{
    text-align:left;
}

@media (min-width: 800px) 
{
    .node .byline
    {
        font-size: 15px;
        display: inline-block;
        width: 76%;
        height: 25px;
        padding-bottom: 7px;
    }
    .social-tools-pane
    {
        display: inline-block;
        width: 240px;
        height: 25px;
    }
	section.body
	{
		-webkit-column-count: 2; /* Chrome */
		-moz-column-count: 2; /* Firefox */
		column-count: 2;
	}
}
.comment-body p
{
	line-height:1.5em;
}
.node .body p
{
	margin:0 10px 20px;
	color:white;
	line-height:1.5em;
}
.body figure
{
    margin:0;
    float: left;
    width: 50%;
    margin: 10px;
}
.body img
{
    width:100%;
}
.author-info
{
    background-color:#303030;
}

.node .byline time,
.node .body h2,
h3, h4,
.short-list li>span,
.popupar-items-pane,
.short-list li>span small,
.footer,
.mini-panel-footer .menu ,
.front-pane h2, .items-pane h2,
.blogs-list h1,
.six-wide.blog-roll .entity,
.threecol-pane>h1,
.more-news .news-pane h2,
.recommend-list h1 a,
.press-release h1 a,
.popular-items-pane a,
.focus-list h1 a,
.short-list h1,
.more-news h1,
.title-no-underline h1,
#mini-panel-comments_and_form h1,
.article-timeline
{
	color: white;
}

#mini-panel-send_us_a_tip h1,
form#comment-form h1,
textarea,
input
{
	color: black;
}

.white-faded::after
{
	background-image:none;
}
  .most-rate-up a,
.most-read-blogs a,
.most-controversial a
{
	color: white;
}

.plugin.right.box
{
	text-align:left;
}
  
}
