css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.dmm.com/netgame/social/-/gadgets/=/app_id=854854/"){
#ntg-recommend,
body>*:not(#w),
#w>div:not(#main-ntg),
#w>img,
#page>div:not(#area-game){
	display:none !important;}
html,body{
	width:100% !important;
	height:100% !important;
overflow:hidden;}
body{
	margin:0 !important;
	min-width:inherit !important;
/*
	background-image や background-color を適宜編集
	あるいは別スタイルを外部から読み込ませてくださいな
*/
	background-color:#333;}
#w{
	display:box;
	box-pack:center;
	box-align:center;
	display:-moz-box;
	-moz-box-pack:center;
	-moz-box-align:center;
	display:-webkit-box;
	-webkit-box-pack:center;
	-webkit-box-align:center;
	width:100% !important;
	height:100% !important;}
#main-ntg{
	width:80px !important;
	height:480px !important;
	margin:inherit !important;
	padding:inherit !important;
	background-color:inherit !important;}
#game_frame{
	width:800px!important;
	height:480px !important;}
}
/* @-moz-document domain("osapi.dmm.com") */
@-moz-document regexp("^http://osapi.dmm.com/.*aid=854854&.*"){
/* div:not(#flashWrap) */
#spacing_top,
/* #adFlashWrap, */
#wsFlashWrap,#sectionWrap,#debugDiv{
	display:none !important;}
#adFlashWrap{
	width:800px !important;}
}
