css:
@-moz-document url-prefix("http://www.york.ac.uk/archaeology/"), url-prefix("http://www.york.ac.uk/biology/"), url-prefix("http://www.york.ac.uk/chemistry/"), url-prefix("https://www.cs.york.ac.uk"), url-prefix("http://www.york.ac.uk/criminology/"), url-prefix("http://www.york.ac.uk/economics/"), url-prefix("http://www.york.ac.uk/education/"), url-prefix("http://www.york.ac.uk/electronics/"), url-prefix("http://www.york.ac.uk/environment/"), url-prefix("http://www.york.ac.uk/healthsciences/"), url-prefix("http://www.york.ac.uk/history/"), url-prefix("http://www.york.ac.uk/history-of-art/"), url-prefix("http://www.york.ac.uk/language/"), url-prefix("http://www.york.ac.uk/law/"), url-prefix("http://www.york.ac.uk/management/"), url-prefix("http://maths.york.ac.uk/"), url-prefix("http://www.york.ac.uk/eighteenth-century-studies/"), url-prefix("http://www.york.ac.uk/english/"), url-prefix("http://www.york.ac.uk/che/"), url-prefix("https://www.york.ac.uk/cahr/"), url-prefix("http://www.york.ac.uk/medieval-studies/"), url-prefix("https://www.york.ac.uk/modernstudies/"), url-prefix("http://www.york.ac.uk/music/"), url-prefix("http://www.york.ac.uk/natural-sciences/"), url-prefix("http://www.york.ac.uk/philosophy/"), url-prefix("http://www.york.ac.uk/physics/"), url-prefix("http://www.york.ac.uk/politics/"), url-prefix("http://www.york.ac.uk/pep/"), url-prefix("http://www.york.ac.uk/prdu/"), url-prefix("http://www.york.ac.uk/psychology/"), url-prefix("http://www.york.ac.uk/railway-studies/"), url-prefix("http://www.york.ac.uk/crems/"), url-prefix("http://www.york.ac.uk/spsw/"), url-prefix("http://www.york.ac.uk/sps/"), url-prefix("http://www.york.ac.uk/sociology/"), url-prefix("http://www.york.ac.uk/tftv/"), url-prefix("http://www.york.ac.uk/inst/cws/") {
@media only screen and (max-width: 962px) {
  body {
    margin:0;
  }
  img {
    max-width:100%;
  }
  div#container {
    width:100%;
  }
  div#header form {
    padding-left:0;
    padding-right:2%;
  }
  div#lhcolumn {
    width:96%;
    padding:0 2%;
    margin-top:12px;
    height:24px;
    overflow:hidden;
  }
  div#lhcolumn:hover,
  div#lhcolumn:focus,
  div#lhcolumn:active {
    height:auto;
  }
  div#lhcolumn:before {
    display:block;
    content:'View more pages';
    width:93%;
    padding-left:5%;
    padding-right:2%;
    background:black;
    color:white;
    line-height:24px;
    height:24px;
  }
  #nav li a,
  #nav li li a,
  #nav li li li a,
  #nav li li li li a,
  #nav li span,
  #nav li li span,
  #nav li li li span,
  #nav li li li li span {
    width:93%;
    padding-left:5%;
    padding-right:2%;
  }
  /* Some get given inline styles of padding:0 */
  #nav li span[style],
  #nav li li span[style],
  #nav li li li span[style],
  #nav li li li li span[style] {
  	width:100%;
  }
  div#mdcolumn {
    width:68%;
    padding-left:2%;
  }
  div.wide#content-container div#mdcolumn {
    width: 96%;
    padding:12px 2%;
  }
  div#homepagebanner {
    width:100%;
    max-width:100%;
    overflow:hidden;
  }
  div#mdcolumn #homepagebanner .imagewithcaption {
    max-width:100%;
  }
  div#mdcolumn div.col-half,
  div#mdcolumn div.col-third {
    margin-left:2%;
  }
  div#mdcolumn div.col-half,
  div.wide div#mdcolumn .col-half {
    width:49%;
  }
  div#mdcolumn div.col-third,
  div.wide div#mdcolumn .col-third {
    width: 32%;
  }
  div#mdcolumn div.first-col {
    margin-left:0;
  }
  div#mdcolumn img {
    width:100%;
    height:auto;
  }
  div#mdcolumn img[height="16"] {
    height:16px;
  }
  div#mdcolumn img[width="16"] {
    width:16px;
  }
  div.wide div#mdcolumn div.col-half .boxout,
  div.wide div#mdcolumn div.col-half .boxoutAlt,
  div.wide div#mdcolumn div.col-third .boxout,
  div.wide div#mdcolumn div.col-third .boxoutAlt{
    width:100%;
  }
  div#mdcolumn .col-half h2, div#mdcolumn .col-third h2 {
    clear:both;
  }
  div#mdcolumn ul.tabNavigation {
    padding-bottom:1px;
  }
  div#mdcolumn ul.tabNavigation li {
    display:inline-block;
    margin-bottom:-2px;
  }
  div#mdcolumn ul.tabNavigation li a {
    display:block;
    white-space: nowrap;
  }
  div#rhcolumn {
    width:25%;
    margin-left:0;
    padding-left:3%;
    padding-right:2%;
  }
  div#rhcolumn blockquote.quote {
    width:92%;
    padding-left:8%;
  }
  #rhcolumn .boxout,
  #rhcolumn .boxoutAlt,
  #rhcolumn .boxoutalt {
    width:92%;
    padding-left:4%;
    padding-right:4%;
  }
  /* Misspelling(?) on http://www.york.ac.uk/archaeology/staff/ */
  #rhcolumn .boxoutalt {
    margin-left:0;
    margin-right:0;
  }
}
/* Go to single column */
@media only screen and (max-width: 588px) {
  div#location {
    width:100%;
  }
  div#header form {
    width:96%;
    padding-left:2%;
  }
  div#header form input, div#header form label {
   float:left;
   margin-left:2%;
  }
  div#header form label {
   margin-top:4px;
  }
  div#header form br {
   display:none;
  }
  div#mdcolumn,
  div#rhcolumn {
    width: 96%;
    padding:12px 2%;
  }
  div#mdcolumn div.col-half,
  div#mdcolumn div.col-third,
  div.wide div#mdcolumn div.col-half,
  div.wide div#mdcolumn div.col-third {
    width: 100%;
    margin-left:0;
  }
}
@media only screen and (max-width: 480px) {
  div#header form br {
   display:block;
   clear:left;
  }
  .right,
  .left 	{
    float:none;
  }
}
}
