css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("questionablecontent.net") {

#table, #side, iframe, #logo, #twitter-widget-0, #aswift_0_expand, #aswift_1_anchor, #topline, #container > div:nth-child(4){
    width: 0px!important;
    display: none!important;
    visibility: hidden!important;
}

/*
#container > center:nth-child(1){
    width: 0px!important;
    display: none!important;
    visibility: hidden!important;
}
*/

#mainnav, #header{
    text-align: center!important;
    padding: unset!important;
    min-width: unset!important;
    width: 100%!important;
}

#container {
    background: #BBCCDD!important;
    min-width: unset!important;
    width: 900px!important;
    margin-left: auto!important;
    margin-right: auto!important;
}

#comicnav {
    text-align: center!important;
    padding: 15px!important;
}

#comic, #comic img, #strip{
    border:none;
    text-align: center!important;
    background: transparent!important;
    width: 100%!important;
    padding-left: unset!important;
    padding-right: unset!important;
}

#news {
    text-align: left!important;
    width: 100%!important;
    padding: unset!important;
    border-top-right-radius: unset!important;
    border-bottom-right-radius: unset!important;
}

}
