css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://browser/content/browser.xul") {

    #main-window {
        background-color: #1883D7 !important;
    }

    #navigator-toolbox > toolbar:not(#toolbar-menubar):not(#TabsToolbar):not(:-moz-lwtheme) {
        background-color: #ffffff !important;
        margin-bottom: -1px !important;
    }

    #titlebar-min {
        list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAABGdBTUEAALGPC/xhBQAAAAlwSFlzAAAOwgAADsIBFShKgAAAABh0RVh0U29mdHdhcmUAcGFpbnQubmV0IDQuMC41ZYUyZQAAACxJREFUKFNj+P//P0kYqyA+jFUQH8YqiA9jFcSHYQxiwbDyNNEYqyBu/J8BAHynuFbd05HGAAAAAElFTkSuQmCC) !important;
    }

    #titlebar-max {
        list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAABGdBTUEAALGPC/xhBQAAAAlwSFlzAAAOwgAADsIBFShKgAAAABh0RVh0U29mdHdhcmUAcGFpbnQubmV0IDQuMC41ZYUyZQAAACpJREFUKFNj+P//P0kYxiAWoGiAsXFhEBjVgCyJDYMAZRqIAXANROL/DAAhmtI8+iNXkwAAAABJRU5ErkJggg==) !important;
    }

    #main-window[sizemode="maximized"] #titlebar-max {
        list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAABGdBTUEAALGPC/xhBQAAAAlwSFlzAAAOwgAADsIBFShKgAAAABh0RVh0U29mdHdhcmUAcGFpbnQubmV0IDQuMC41ZYUyZQAAAERJREFUKFOVjQEKACAIA/3/p40FDlmj8uBYHEiRmSM1vLAH2krAgxf2oN4q4Gp0Aq5GJ+BqdAKuRmdn9MO2xxvHwacZC5DT2zMlWISEAAAAAElFTkSuQmCC) !important;
    }
    /*download button*/
    #downloads-button {
        background: url(data:image/png;base64,) !important;
        background-repeat: no-repeat !important;
        background-position: center !important;
        margin-left: 0px !important;
        margin-right: 0px !important;
        margin-top: 7px !important;
        margin-bottom: 7px !important;
        width: 10px !important;
    }

        #toolbarbutton-icon .Downloads,
        #downloads-indicator-icon,
        #downloads-indicator-progress-area,
        #downloads-button .button-text {
            display: none !important;
        }

    #downloads-indicator-anchor {
        opacity: 0 !important;
        margin-top: 8px;
    }

    #downloads-button:hover {
        background: #e5e5e5 url(data:image/png;base64,) !important;
        background-repeat: no-repeat !important;
        background-position: center !important;
    }

    #downloads-button:active {
        background: #1883D7 url(data:image/png;base64,) !important;
        background-repeat: no-repeat !important;
        background-position: center !important;
    }

    #titlebar-close,
    #titlebar-close:hover {
        list-style-image: url(data:image/png;base64,) !important;
    }
    /* reset regions to 0, so custom icons are aligned */
    #back-button > .toolbarbutton-icon,
    .urlbar-history-dropmarker,
    #urlbar > toolbarbutton,
    #urlbar-reload-button:not([disabled]):hover,
    #urlbar-reload-button:not([disabled]):hover:active,
    #urlbar-stop-button,
    #urlbar-stop-button:hover:active,
    #urlbar-stop-button:not([disabled]):hover,
    #urlbar-go-button,
    #urlbar-go-button:hover,
    #urlbar-go-button:hover:active,
    #PanelUI-menu-button .toolbarbutton-icon {
        -moz-image-region: rect(0, 0, 0, 0) !important;
    }
        /* remove list-style-image, contain and center any future background-images */
        #urlbar-reload-button,
        #urlbar-reload-button:not([disabled]):hover,
        #urlbar-reload-button:not([disabled]):hover:active,
        /*#downloads-button,
         #downloads-indicator-anchor,
        #downloads-indicator-icon,*/
        #urlbar-stop-button,
        #urlbar-stop-button:hover:active,
        #urlbar-stop-button:not([disabled]):hover,
        #urlbar-go-button,
        #urlbar-go-button:hover,
        #urlbar-go-button:hover:active,
        #PanelUI-menu-button .toolbarbutton-icon,
        .tabs-newtab-button .toolbarbutton-icon {
            list-style-image: none !important;
            background-size: contain !important;
            background-position: center center !important;
            background-repeat: no-repeat !important;
        }

    #urlbar:not(:-moz-lwtheme), .searchbar-textbox:not(:-moz-lwtheme) {
        background-color: #E5E5E5 !important;
    }

    #back-button > .toolbarbutton-icon {
        background-color: #1883D7 !important;
        opacity: 0.9 !important;
        list-style-image: url(data:image/png;base64,) !important;
        box-shadow: none !important;
    }

    #back-button:not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon {
        background-color: #1883D7 !important;
        opacity: 1 !important;
    }

    #page-proxy-favicon {
        list-style-image: url(data:image/png;base64,);
        background-size: contain !important;
    }

    .urlbar-history-dropmarker {
        margin-right: 4px !important;
        list-style-image: url(data:image/png;base64,);
    }

        .urlbar-history-dropmarker .dropmarker-icon {
            width: 16px !important;
            opacity: 0.33 !important;
        }

    #urlbar-reload-button,
    #urlbar-reload-button:not([disabled]):hover,
    #urlbar-reload-button:not([disabled]):hover:active {
        width: 16px !important;
        height: 16px !important;
        background-image: url(data:image/png;base64,);
        background-size: 80% !important;
    }

    #urlbar-reload-button {
        opacity: 0.33;
    }

        #urlbar-reload-button:not([disabled]):hover,
        #urlbar-reload-button:not([disabled]):hover:active {
            opacity: 0.5;
        }

    #urlbar-stop-button,
    #urlbar-stop-button:hover:active,
    #urlbar-stop-button:not([disabled]):hover {
        width: 16px !important;
        height: 16px !important;
        background-image: url(data:image/png;base64,) !important;
    }

    #urlbar-go-button,
    #urlbar-go-button:hover,
    #urlbar-go-button:hover:active {
        width: 16px !important;
        height: 16px !important;
        background-image: url(data:image/png;base64,) !important;
    }

    #urlbar-go-button {
        opacity: 0.33;
    }

        #urlbar-go-button:not([disabled]):hover,
        #urlbar-go-button:not([disabled]):hover:active {
            opacity: 0.5;
        }

    .tabs-newtab-button .toolbarbutton-icon {
        width: 16px !important;
        height: 16px !important;
        background-image: url(data:image/png;base64,) !important;
        background-size: 100% !important;
        margin-left: 3px !important;
        margin-right: 2px !important;
        margin-top: -1px !important;
    }

    #TabsToolbar .tabs-newtab-button {
        opacity: 1;
    }

        #TabsToolbar .tabs-newtab-button:hover {
            background: #1573BD !important;
        }

        #TabsToolbar .tabs-newtab-button:active {
            background: #1366A8 !important;
        }
    /* download button is really annoying so forget it for now
  #downloads-button .toolbarbutton-icon {
    background-image: url(data:image/png;base64,) !important;
  }
  */
    #PanelUI-menu-button .toolbarbutton-icon {
        width: 32px !important;
        height: 32px !important;
        background-image: url(data:image/png;base64,) !important;
    }
    /*Click image for menu: data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAB4AAAAeCAYAAAA7MK6iAAAABGdBTUEAALGPC/xhBQAAAAlwSFlzAAAOwQAADsEBuJFr7QAAABh0RVh0U29mdHdhcmUAcGFpbnQubmV0IDQuMC41ZYUyZQAAAFpJREFUSEvt0CESACEMBEH+/+mcQbbkBkOq2oxYkTUzVzAWGAuMBcYCY4GxwLidOm2/V3cYC4wFxgLjduq0/V7dYSwwFhgLjNup0/Z7dYexwFhgLDAWGP836wNR6bfVx+UpHQAAAABJRU5ErkJggg== */
    /* https://userstyles.org/styles/99560/square-tab-for-firefox-australis */
    #TabsToolbar {
        background: transparent !important;
        margin-bottom: 0 !important;
    }

        #TabsToolbar .arrowscrollbox-scrollbox {
            padding: 0 !important;
        }

        #TabsToolbar .tabbrowser-tabs {
            border-right: solid 1px rgba(0,0,0,0.0) !important;
            min-height: 26px !important;
            margin-left: 20px !important;
        }

        #TabsToolbar .tabbrowser-tab {
            border-right: solid 1px rgba(0,0,0,0.0) !important;
            -moz-border-top-colors: none !important;
            -moz-border-left-colors: none !important;
            -moz-border-right-colors: none !important;
            -moz-border-bottom-colors: none !important;
            color: #ffffff !important;
            padding: 4px 0px !important;
            background: rgba(255,255,255,.27) !important;
            background-clip: padding-box !important;
            transition: all .1s !important;
        }

        #TabsToolbar .tabs-newtab-button {
            -moz-border-top-colors: none !important;
            -moz-border-left-colors: none !important;
            -moz-border-right-colors: none !important;
            -moz-border-bottom-colors: none !important;
            border-style: solid !important;
            border-color: transparent !important;
            border-width: 1px 1px 0 1px !important;
            background: transparent !important;
            background-clip: padding-box !important;
            transition: all .1s !important;
        }

        #TabsToolbar .tabbrowser-tab[afterselected] {
            border-left-color: rgba(0,0,0,0.0) !important;
        }

        #TabsToolbar .tabbrowser-tab[selected] {
            color: #696969 !important;
            background: #FFFFFF !important;
            background-clip: padding-box !important;
        }

        #TabsToolbar .tabbrowser-tab:hover:not([selected]) {
            background-color: rgba(255,255,255,.55) !important;
        }

        #TabsToolbar .tabbrowser-tab:not([selected]):not([last-tab]) {
            border-right: solid 1px rgba(0,0,0,0.0) !important;
        }

        #TabsToolbar .tab-background {
            margin: 0 !important;
            background: transparent !important;
        }

        #TabsToolbar .tab-background-start,
        #TabsToolbar .tab-background-end {
            display: none !important;
        }

        #TabsToolbar .tab-background-middle {
            margin: -4px -2px !important;
            background: transparent !important;
        }

        #TabsToolbar .tabbrowser-tab:after,
        #TabsToolbar .tabbrowser-tab:before {
            display: none !important;
        }

        #TabsToolbar .tabs-newtab-button {
            border-width: 1px 1px 0 1px !important;
            margin: 0 !important;
            width: auto !important;
            padding: 0 5px !important;
        }

    #addon-bar,
    #PersonalToolbar,
    #nav-bar {
        background: #F7F7F7 !important;
        box-shadow: none !important;
        background-clip: padding-box !important;
        border-color: rgba(0,0,0,.25) !important;
        border-radius: 0 !important;
    }

    #menu_pocket,
    #menu_pocketSeparator,
    #panelMenu_pocket,
    #BMB_pocket,
    #BMB_pocketSeparator,
    #context-savelinktopocket,
    #context-pocket {
        display: none !important;
    }
}
