css:
@-moz-document domain("f-picture.net") {
iframe, .footer, #IdBlockForSetCookie, .logo, .base-page_left-side, .banerRight, .spacer, .pad-20, .banerTop, .menuTop, .banerBottom, .divBottomBlock, .pict-menu, .pict-menu ~ div {
    display: none !important;
}

body {
    overflow: hidden;
}

.f-content {
    box-shadow: none !important;
    margin: 0 !important;
    padding: 0 !important;
}

.base-page_center, .base-page_wrapper {
    margin: 0 !important;
    padding: 0 !important;
    width: 100vw !important;
    float: none !important;
}

div[itemtype="http://schema.org/ImageObject"] {
    height: 0 !important;
}

div[itemtype="http://schema.org/ImageObject"] img {
    max-width: 100vw !important;
    max-height: 100vh !important;
}
}
