css:
@-moz-document domain(55ch.org), domain(8ch.net), domain(55chn.org), domain(55chan.org) {

body {
    background: black !important;
    color: white;
}div.post.reply{
    background: #1B1B1B none repeat scroll 0% 0%;
    border-width: 0px;
    padding: 5px 5px 5px 5px;
}
.unimportant{
color: white;
font-size:10pt;
}
.theme-catalog div.thread:hover {
    background: #3D3D3D none repeat scroll 0% 0%;
    border-color: grey;
}
div.post.reply.highlighted{
    background: #3D3D3D none repeat scroll 0% 0%;
    border-width: 0px;
    padding: 5px 5px 5px 5px;
}
.desktop-style div.boardlist:nth-child(1), div.banner{
    background-color: #2E2E2E;
}
div.boardlist {
    color: white;
}
form table tr th {
    background: #2E2E2E none repeat scroll 0% 0%;
}
.desktop-style div.boardlist.first {
    border-bottom: 0px solid;
    color: white;
}
a:link{
    color: white;
    text-decoration: none;
}
a:link:hover{
    color: #3D3D3D;
    text-decoration: none;
}
div.post.reply div.body a {
    color: white;
}
div.boardlist a {
    color: white;
    font-weight: bold
}
h1, h2{
 color: white;
}
 
.intro span.name {
    color: white;
    font-weight: bold;
}
.options_tab, #options_tablist{
background-color: #2E2E2E;
}
.options_tab_icon.active {
    color: White;
}
span.quote {
    color: #A3A3A3;
    font-weight: bold;
    font-size: 10.2pt;
}
span.heading {
    color: #A3A3A3;
    font-size: 12pt;
    font-weight: bold;
}
 
.intro a.email span.name {
    color: #A3A3A3;
}
.intro a.email:hover span.name {
    color: white;
}
.intro span.subject {
    color: #A3A3A3;
    font-size: 12pt;
}
}
