css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.mangatown.com")
{
  script,iframe,.copyright,.adv-728-top,.adv-360-top,.adv300,.ad960,.adv-here,.adv-footer,footer ul,.mangaread-main .title
  {
    display: none!important;
    height: 0!important;
    width: 0!important;
    visibility: hidden!important;
    opacity: 0!important;
		position:absolute!important;
		left:-200%!important;
  }
.mangaread-footer
	{
		text-align: center;
	}
	.mangaread-footer{padding:0;height:40px;}
  .mangaread-main
  {
    padding: 0;
    min-height:500px;
  }

	.read-btn{float:none!important;margin:0;height:40px;width:100%;display:inline-block;}

.read-btn a {
  background:#e1615d;
	border: 0;
	padding: 15px 0!important;;
  text-align: center;
  margin:0;
  display:block;
  width:33%;
  float:left;
	text-transform: lowercase
}
 .read-btn #bookmark{background:#fdbd57;width:34%!important;}
  .read-btn #subscribe{background:#69c773;}
  .sidebar
  {
    position: fixed;
    right: 3px;
    top: 3px;
    width: 35px;
    height: 35px;
    z-index: 10;
  }

  .sidebar i
  {
    width: 35px;
    height: 35px;
    background-color: #374249;
    color: #fff;
    border-radius: 50px;
    display: block;
    text-align: center;
    line-height: 35px;
    margin-bottom: 5px;
    font-size: 18px;
  }

  .mangaread-img
  {
    margin: 0 auto;
    padding: 0;
    position: relative;
    text-align: center;
    width: 100%!important;
  }

  .mangaread-img img
  {
    width: 100%;
  }

  .page-read
  {
    padding: 15px 10px!important;
  }

  .post
  {
    border-bottom: 0;
  }
}
