@-moz-document regexp("http(s)?://(forum((\\d{1,2})|d)?|archive|search)\.hkgolden\.com/(topics|Search|search).*"){
    /*topic style*/
    tr[id*=Thread]>td{color:#000!important;}
    tr[id*=Thread]>td:nth-of-type(1){display:none!important;}
    tr[id*=Thread]>td:nth-of-type(2){width:77%!important;}
    tr[id*=Thread]>td:nth-of-type(2)>a:first-child   {color:#c60!important;font-size:26px!important;line-height:105%!important;padding-left:10px!important;}
    tr[id*=Thread]>td:nth-of-type(2)>a:nth-child(n+2){color:#c60!important;font-size:12px!important;}
    tr[id*=Thread]>td:nth-of-type(3){width:18%!important;}
    tr[id*=Thread]>td:nth-of-type(3)>a               {color:#ccc!important;font-size:26px!important;line-height:100%!important;}
    tr[id*=Thread]>td:nth-of-type(4){display:none!important;}
    tr[id*=Thread]>td:nth-of-type(5){display:none!important;}
    tr[id*=Thread]>td:nth-of-type(6){width:5%!important;}
    tr[id*=Thread]>td:nth-of-type(6)                 {color:#a11!important;font-size:26px!important;line-height:100%!important;}
    
    /*main collapse*/.PageMiddleBox{padding:0px!important;}
    
    /*menu dropdown*/select{background:#000!important;color:#ccc!important;font-size:22px!important;}
    /*menu dropdown*/option{background:#030!important;}
    /*menu dropdown*/option:nth-child(2n){background:#020!important;}
    /*menu dropdown forum*//*#forum_list>option:nth-child(5n+1){background:#060!important;}*/
    /*menu dropdown search*//*#getSerachType>option:nth-child(5n+3){background:#060!important;}*/
    /*menu dropdown pages*/#page>option:nth-child(5n){background:#060!important;}
    /*input search all*/input#q,
    /*input search topics*/input#searchstring{background:#333!important;color:#0a0!important;}
    
    /*thread BG*/#mainTopicTable tr:nth-child(2n+1){background:#040!important;}
    /*thread BG*/#mainTopicTable tr:nth-child(2n+0){background:#030!important;} 
    /*thread BG*/#mainTopicTable tr:hover{background:#000!important;}
    
    
    
    .Topic_FunctionPanel,
    .Topic_FunctionPanel>*{margin-top:0px!important;}
    
    /*top menu*/#aspnetForm>*:not(#PageMiddlePanel),
    /*top blank collapse*/.PageMiddleFunctions,
    /*top Featured Articles*/.ContentPanel>table[width*="954px"]>tbody>tr>td>*:not([style*=bold]),
    /*top second hand trade*/#secondHand_list,div.ContentPanel tr>td[align="right"][style*="310"],
    /*top ad link*/.Topic_FunctionPanel>div:last-of-type>a,
    /*top ad hot search words*/span.HitSearchText,
    /*main table th*/#HotTopics>span,#mainTopicTable>tbody>tr:nth-of-type(1),
    /*main table th*/#mainTopicTable>tbody>tr:nth-of-type(2),
    /*main table ad*/.ContentPanel>table>tbody>tr>td>*>br,#mainTopicTable>tbody>tr>td[height="70"],
    /*main table ad*/#mainTopicTable>tbody>tr:not([id*="Thread_"]),
    /*bottom rule*/.ContentPanel>*:nth-child(n+8):not([style*="background-color: #000000;"]){
        display:none!important;}   
}
