*::-webkit-scrollbar-track-piece      {background:#000!important;}
*::-webkit-scrollbar-track-piece:hover{background:#333!important;}
*::-webkit-scrollbar-thumb            {background:#357!important;}
*::-webkit-scrollbar-thumb:hover      {background:#168!important;}
::selection     {background:#550!important;color:#fff!important;font-weight:bold!important;}
::-moz-selection{background:#550!important;color:#fff!important;}
img                {-webkit-user-select:none!important;-moz-user-select:none!important;-ms-user-select:none!important;}
img::selection     {background-color:transparent!important;}
img::-moz-selection{background-color:transparent!important;}
img:before         {-webkit-user-select:none!important;-moz-user-select:none!important;-ms-user-select:none!important;}
img:after          {-webkit-user-select:none!important;-moz-user-select:none!important;-ms-user-select:none!important;}

    /*remove - text style*/*{text-shadow:none!important;text-decoration:none!important;}
