    .TOP-msg{background:transparent!important;border:6px solid #000!important;border-radius:0px 0px 20px 20px!important;}
    .TOP-msg>span{color:#a11!important;background:#000!important;text-align:center!important;border-radius:0!important;}
    .TOP-msg>.TOP-msglist{max-height:60vh!important;background:transparent!important;color:#ccc!important;border-radius:0!important;}
    .TOP-msg>.TOP-msglist>div{background:#040!important;padding:0px!important;overflow:hidden!important;}
    .TOP-msg>.TOP-msglist>div:nth-child(2n){background:#030!important;}
