    .TOP-msg>.TOP-msglist>div:hover{background:#000!important;}
    .TOP-msg>.TOP-msglist>div>a{font:0/0 arial!important;width:100%!important;}
    .TOP-msg>.TOP-msglist>div>a>.TOP-msgpic{width:70px!important;height:70px!important;margin:0!important;}
    .TOP-msg>.TOP-msglist>div>a>.TOP-msgpic>img{width:100%!important;height:100%!important;max-width:initial!important;max-height:initial!important;}
    .TOP-msg>.TOP-msglist>div>a>.msgname{}
    .TOP-msg>.TOP-msglist>div>a>.title{padding:12px 8px 0px!important;margin:0 0 0 75px!important;}
    .TOP-msg>.TOP-msglist>div>a>.title>.link{font:18px/120% arial!important;color:#c60!important;}
    .TOP-msg>.TOP-msglist>div>a:visited>.title>.link{color:#660!important;}
    .TOP-msg>.TOP-msglist>div>a>div{margin:0!important;right:0px!important;bottom:3px!important;position:absolute!important;}
    .TOP-msg>.TOP-msglist>div>a>div>.time{font:14px/100% arial!important;color:#666!important;margin:0 6px 0 0!important;right:0px!important;position:relative!important;text-align:right!important;display:block!important;}
