    .TOP-msg>.TOP-msglist#topBarMsgList_light_0>div>a:hover>.time{color:#0a0!important;}
    
    /*search*/
    .TOP-bh>#BH-top-data>a[href="https://www.gamer.com.tw/"]{}
    .TOP-bh>#BH-top-data>.TOP-search{}
    .TOP-bh>#BH-top-data>.TOP-search .gsc-input{font:14px/26px arial!important;color:#ccc!important;background:#333!important;}
