    /*2nd menu - popdown*/ul.BH-menuE dl{background:#030!important;}
    /*2nd menu - collapse*/.BH-menu{padding-top:0px!important;background:transparent!important;}
    
    /*user message*/.frame{background:#050!important;}
    /*user message ad*/body>#BH-background~div[style*="position: fixed; left: 20px; right: 20px; bottom: 20px;"]{display:none!important;}




/*daily login*/
dialog.popup-dailybox{background:#241!important;border:3px solid #000!important;}
dialog.popup-dailybox>.dialogify__content{background:transparent!important;padding:0 10px!important;}
dialog.popup-dailybox>.dialogify__content>*{}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body{}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__title     {margin:0 auto 10px!important;}
