dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__subtitle>p{font:20px/100% arial!important;color:#ccc!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__subtitle>p:nth-child(1)     {color:#c60!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__subtitle>p:nth-child(1)>span{color:#aa1!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__subtitle>p:nth-child(2){}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox{}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day.dailybox-day--header       {background:#000!important;padding:4px 0 2px!important;margin:0!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day.dailybox-day--header::after{display:none!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day.dailybox-day--header>p     {font:20px/100% arial!important;color:#a11!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day:not(.dailybox-day--header)       {background:#333!important;padding:0!important;margin:0!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day:not(.dailybox-day--header)::after{background-color:transparent!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day:not(.dailybox-day--header)>.dailybox-day__img{}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day:not(.dailybox-day--header)>.dailybox-day__img>img{}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.dailybox>.dailybox-day:not(.dailybox-day--header)>p{font:20px/100% arial!important;color:#ccc!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__bottom             {padding:0!important;margin:10px auto 10px!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__bottom>button      {background:#234!important;font:20px/100% arial!important;color:#c60!important;padding:8px 20px 4px!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__bottom>button:hover{background:#424!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__bottom>p           {font:20px/100% arial!important;color:#ccc!important;}
dialog.popup-dailybox>.dialogify__content>*>.dialogify__body>.popup-dailybox__bottom>p>a         {color:#c60!important;}
dialog.popup-dailybox>.dialogify__close      {display:none!important;background:#000!important;padding:7px 7px 5px 8px!important;border-radius:50%!important;}
dialog.popup-dailybox>.dialogify__close:hover{background:#424!important;}
dialog.popup-dailybox>.dialogify__close>img  {opacity:1!important;}
dialog.popup-dailybox>.resize-sensor{}


}
