	background: rgba(31,31,31,0.6);
	border-left: 1px solid #555;
	border-bottom: 1px solid #555;
	border-radius:0px;
	color:#FFF;
}
.accountSideBar .secondaryContent {
	background:rgba(31,31,31,0.6);
	border-left: 1px solid #555;
	border-radius:0px;
	color:#FFF;
	text-shadow: 0 0 4px #000, 0 0 4px #000, 0 0 4px #000, 0 0 4px #000, 0 0 4px #000, 0 0 4px #000, 0 0 4px #000;
}
.accountSideBar .primaryContent:hover {
	background: rgba(41,41,41,1) !important;
	border-left: 1px solid #555;
	border-radius:0px;
	color:#FFF;
}
.accountSideBar .secondaryContent:hover {
	background:rgba(41,41,41,1) !important;
	border-left: 1px solid #555;
	border-radius:0px;
	color:#000;
	text-shadow: 0 0 2px #FFF, 0 0 2px #FFF, 0 0 2px #FFF, 0 0 2px #FFF, 0 0 2px #FFF, 0 0 2px #FFF, 0 0 2px #FFF;
}
.accountSideBar .subHeading {
	background:rgba(31,31,31,0.6);
	border-left: 2px solid #555;
	border-bottom: 2px solid #555;
	border-radius:0px;
}
.explain .CurrentStatus {
	color: #FFF;
}
.alerts .primaryContent {
	background: rgb(21,21,21);
	border:1px solid #555;
	border-radius:10px;
}
.event.primaryContent.NewsFeedItem {
	background: rgb(21,21,21);
	border:1px solid #555;
	border-radius:10px;
}
.primaryContent.memberListItem {
	background: rgb(21,21,21);
	border:1px solid #555;
	border-radius:10px;
}
}

