/* Transition page */
.transactionListItem { 
	background: rgb(25,25,25); 
  background: -moz-linear-gradient(top, rgba(25,25,25,1) 0%, rgba(25,25,25,1) 65%, rgba(22,22,22,1) 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(25,25,25,1)), color-stop(65%,rgba(25,25,25,1)), color-stop(100%,rgba(22,22,22,1)));
}
.discussionListFilters.secondaryContent {
	background: rgb(31,31,31);
	border-bottom: none;
}
