css:
/**
 * Dark gray style for LOR
 * (c) 2008 friday
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.linux.org.ru/){
	* {
		font-family: sans-serif !important;
		font-size: 9pt;
		color: #a0a0a0 !important;
		background: none !important;
	}
	a[href="/"] > img {
		display: none;
	}
	a {
		text-decoration: none;
		color: #e0e0e0 !important;
	}
	a:hover {color: #ffffff !important;}
	a:visited {color: #c0c0c0 !important;}
	
	h1 {
		font-size: 12pt !important;
		color: #c0c0c0 !important;
		padding: 0px 8px !important;
	}
	body {
		background-color: #404040 !important;
	}
	body > p {
		padding: 0px 8px !important;
	}
	
	.nav {
		border: 1px solid #a0a0a0 !important;
		background-color: #282828 !important;
	}
	.nav * {
		background: none !important;
	}
	.nav a {
		background: none !important;
	}
	.nav span a {
		background: none !important;
	}
	.nav td {
		padding: 0px 8px !important;
		border-width: 0px !important;
	}
	
	table.message-table {
		border: 1px solid #808080;
		border-collapse: collapse;
	}
	table.message-table th {
		border: 1px solid #a0a0a0 !important;
		background-color: #282828 !important;
		padding: 0px 8px !important;
	}
	table.message-table thead * {
		background-color: #282828 !important;
	}
	
	table.message-table tbody tr {background: none !important;}
	table.message-table tbody tr:hover {background-color: #343434 !important;}
	table.message-table tbody tr td {
		background: none !important;
		border: 1px solid #a0a0a0 !important;
		padding: 0px 8px !important;
	}
	table.message-table tbody tr td * {
		background: none !important;
	}
	
	img[src="/black/lorlogo.png"], img[src="/black/pingvin.gif"], img[src="/white2/h1.png"], img[src="/white/linux_main.gif"] {
		display: none;
	}
	
	div.head-main table {
		border-width: 0px !important;
	}
	div.head-main table tbody tr td {
		border-width: 0px !important;
	}
	
	div.boxlet {
		border: 1px solid #a0a0a0 !important;
		padding: 0px 8px 8px 8px !important;
		width: 222px !important;
	}
	div.boxlet h2 {
		font-size: 9pt !important;
		color: #c1c1c1 !important;
		text-align:left !important;
		border-bottom: 1px solid #a0a0a0 !important;
		background-color: #282828 !important;
		margin: 0px -8px 8px -8px !important;
		padding: 0px 8px !important;
		width: 222px !important;
	}
	div.boxlet h2 a {
		background: none !important;
	}
	div.boxlet iframe {
		width: 200px !important;
	}
	
	div.news {
		background: none !important;
		border: 1px solid #a0a0a0 !important;
		margin-bottom: 4px;
		padding: 0px 8px 8px 8px !important;
	}
	div.news h2 {
		font-size: 9pt !important;
		color: #c1c1c1 !important;
		text-align:left !important;
		border-bottom: 1px solid #a0a0a0 !important;
		background-color: #282828 !important;
		margin: 0px -8px 8px -8px !important;
		padding: 0px 8px !important;
	}
	div.news h2 a {
		background: none !important;
	}
	div.news div.msg {
		border-width: 0px !important;
	}
	div.news div.nav {
		font-size: 12pt !important;
		color: #c0c0c0 !important;
		border-width: 0px !important;
		background: none !important;
	}
	
	div.msg {
		background: none !important;
		border: 1px solid #a0a0a0 !important;
		margin-bottom: 4px;
		padding: 0px 8px 8px 8px !important;
	}
	div.msg h2 {
		font-size: 9pt !important;
		color: #c1c1c1 !important;
		text-align:left !important;
		border-bottom: 1px solid #a0a0a0 !important;
		background-color: #282828 !important;
		margin: 0px -8px 8px -8px !important;
		padding: 0px 8px !important;
	}
	
	input[type="text"], textarea {
		background: #808080;
		color: #e0e0e0;
	}
}
