css:
@-moz-document domain("duckduckgo.com"){

body, .search--home .acp-wrap, .search--home.has-text .search__button, .search__button:hover, .nav-menu__list, .btn, .cloudsave, .frm__select select, .frm__select, .header-wrap, .search--header.has-text.search--hover .search__button, .acp-wrap, .search__button, #header.header.cw, #header.header.cw::after, #header_wrapper.header-wrap.js-header-wrap::after, .tile__body, .tile--m, .btn--top, div.tile.tile--info.js-infobox-tile, .tile__expand, #zci-about, #zci-definition, #zci-videos, .content-wrap--home, #zci-stocks, .zci.is-active, .detail__pane
  {background-color: black !important;}

.tileview .metabar--fixed, .tile-wrap, .result.highlight, .tile-nav.can-scroll, .tile-nav.can-scroll:hover, .tile-nav.can-scroll::after, .acp--highlight, .frm__select select option--focus, .tile__foot--news, .msg--result.highlight, div.result.results_links.results_links_deep.web-result.result:hover
  {background-color: #191919 !important;}

.search--home.has-text .search__button, .nav-menu__item > a, .btn, .set-thumbnail__label, span, .cloudsave, .frm__select select, .tile__body
  {color: #d2d2d2 !important;}

.nav-menu__heading span, .set-head__title, .frm__label, .tile__title, #search_form_input
  {color: #a9a9a9 !important;}

.btn.is-active
  {text-decoration: underline !important;}

.cloudsave a, a, .set-main .frm__section-label
  {color: white !important;}

.nav-menu__list, .btn, .cloudsave, .btn--top
  {border: 1px solid #a9a9a9 !important;}

.btn--top:hover
  {background: #333333 !important;}

.search, .search--adv {
  background-color: black !important;
  border: 1px solid #a9a9a9 !important;
  color: #d2d2d2 !important;}

.header-wrap {border-top: unset !important;}

.logo-wrap--home, .tag-home--slide, .foot-home, div.badge-link, span.header__logo, .metabar--fixed, .site-wrapper-border, .is-not-mobile-device .site-wrapper-border, .header-wrap--home, .msg.msg--box.msg--result {
  display: none !important;}
}





@-moz-document domain(google.com) {.gb_ga.gb_g {display: none !important;}}
