@-moz-document domain("bunker.lunavod.ru") {
.topic {
    padding: 15px;
    overflow: hidden;
    zoom: 1;
    margin-bottom: 50px;
    border: 1px solid #E0E0E0;
    border-radius: 2px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
.avatar {border-radius: 50%; border: 0px solid gray;}
.nav.nav-main {
    background: #2733A9;
}
.nav.nav-main li a:hover {
    background: #3F51B5;
}
.nav.nav-main li.active a {
	background: #3F51B5;
}
.topic .topic-content.text {
    color: #212121;
}
#wrapper {
    background: #EBECFF;
}
#content {
    background: #EBECFF;
}
.topic {
    background: white;
}
.block .block-header {
    background: #2733A9;
}
.comment-avatar {
    border: 0px solid #E6E6E6;
    border-radius: 50%;
}
.nav.nav-filter li a.new {
    background: #E6E6E6;
    font-weight: bold;
}
.nav.nav-filter li.active a {
    background: #E6E6E6;
    color: inherit;
}
.nav.nav-filter li.active a.new {
    background: #D8D8D8;
}
.nav.nav-filter li.active a.new:hover {
    background: #D8D8D8;
}
.comment {
    background: white;
}

.button.button-primary.active {
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}
.button.button-primary {
    background: #3E50B4;
}
body {
    background-color: #240C64;
}
.logo {
	margin-bottom: -5px;
}
.nav.nav-main {
    border-radius: 0px;
    border: none;
}
.nav.nav-filter.nav-filter-sub li.active a {
    color: #777;
}
.comment.comment-self {
    background: rgba(102, 53, 204, 0.07);
}
.profile .vote {
    position: absolute;
    top: 0px;
    right: 140px;
    background: #EEE;
}
.toolbar section {
    border: 2px solid #E0E0E0;
}
.comment.comment-current {
    background: #FFDEDE;
}
.block .block-header h3 {
    color: white;
}
