.block .block-header h3 a{
    color: white;
}
#container {
    margin-bottom: 40px;
}
.stat-performance {
    display: none;
}
#userbar {
    border-radius: 0px;
}
.search .input-text {
    display: none;
}
.search .input-submit:hover {
    display: none;
}
.search .input-submit {
    display: none;
}
