css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lesswrong.ru") {
  .container{
    width: 1060px !important;
  }
  
  /* ESI 2.5.1 core */
  html {font-size: 16px !important; line-height: 136% !important}
  esi-widest-text-block {max-width: 90% !important}
  a {text-decoration: none !important}
  esi-text-block {width: x rem; height: y rem}	
  * {
    font-size: inherit !important; 
    line-height: inherit !important;
    font-family: Tahoma, serif;
    text-align: justify !important;
  }
  h1 {font-size: 1.7 rem !important; font-size: calc(1.4375rem + 8px) !important;
    line-height: 120% !important; line-height: calc(1.4375rem * 1.052 + 8px * 1.052 + 5px) !important}
  h2 {font-size: 1.4 rem !important; font-size: calc(1.25rem + 4px) !important;
    line-height: 125% !important; line-height: calc(1.25rem * 1.052 + 1.052 + 4px + 5px) !important}
  h3 {font-size: 1.17 rem !important; font-size: calc(1.0625rem + 2px) !important;
    line-height: 130% !important; line-height: calc(1.0625rem * 1.052 + 2px * 1.052 + 5px) !important}
  sup, sub{line-height: 1 !important; font-size: 0.6875rem; font-size: calc(1rem - 5px) !important;}
  /*ESI core end*/
  
}
