css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/followers"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/liked"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/followers"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/liked"), url-prefix("http://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/tagged") {
/*columns background*/
.l-content {
background:none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
/*background*/
body.logged_in, body.logged_out {
background: url('http://i62.tinypic.com/1zyfznk.png') bottom left #f9fafa       no-repeat fixed !important;
background-size:auto!important;
}

/*posts background color*/
.post, .notification_pane, .radar_controls {
background:rgba(255,255,255,1)!important;
}

/*navigation bar's background and "compose" button*/
.l-header-container {
box-shadow:none!important;
background:rgba(0,0,0,0.2)!important;
position:absolute!important;
}

.compose-button {
border:none!important;
background:rgba(255,255,255,0.3)!important;
color:#ddd!important;
}

a.post_permalink, .date_header {
background:#000000!important;
}

.ui_note.is_friend {
border-color:#cfcfcf!important;
}

a.post_tag:hover, 
a.post_info_link:hover {
color:#000000!important;
}

.post_permalink {
border-color: transparent!important;
background-color: rgba(207,207,207,.5)!important;
border-radius:0px 3px 0px 15px!important;
}

/*posts text color*/
.post_body,
.post_body a,
a.post_tag,
a.post_info_link,
.post_source_name_prefix,
.new_post_label,
.heading,
.post_info,
.note_link_current,
.quote,
.post_title {
color:#000000!important;
}

.note_item, .note_item .name, strong {
color:grey!important;
}

/*right column text color*/
li.section_header,
a.currently_selected_blog,
a.open_blog_link,
a.posts div.hide_overflow,
a.followers div.hide_overflow,
a.activity div.hide_overflow,
a.queue div.hide_overflow,
a.customize div.hide_overflow,
a.likes div.hide_overflow,
a.following div.hide_overflow,
a.spotlight div.hide_overflow,
a.drafts div.hide_overflow,
a.has_follow_button span, a.has_follow_button small,
span.count,
#right_column i,
a.controls_item_anchor .hide_overflow,
.radar_tumblelog_label .name,
.icon_star_12,
.radar_tumblelog_label .subheading,
.small_links a,
.messages, .delete_all_messages, .no_push .message,
right_column .tag,.track_tag,
.search_control_items span, .search_control_items i,
#search_query, .search_query,
span.follow_icon, a.icon_plus,
#dashboard_controls a,
#dashboard_controls .control,
.dashboard_controls_arrow_right,
.dashboard_controls_arrow_left {
color:#000!important;
opacity:0.6 !important;
}

.recommended_tumblelogs, #tumblr_radar, .radar_header {
display:all!important;
}

/*remove blue and white bits*/
#right_column:after, 
#right_column,
#dashboard_index #left_column,
#dashboard_messages_inbox #left_column,
#dashboard_posts_likes #left_column,
.post_content, 
.post_tags:after, 
.section.first.divider,
blockquote pre,
#tumblr_radar,
.controls_item:hover,
.controls_section_item:hover,
ul.controls_section li.item:hover,
#xinbox_sidebar :hover {
background:none !important;
}

.icon_close {
margin-left:6px!important;
}

.new_post_notice_container {
border:none!important;
background:#fff!important;
padding-top:2px!important;
}

.tab_notice_value {
color:white!important;
}

.search_query {
background:rgba(255,255,255,0.2)!important;
border:none!important;
}

.post_tags.no_pop {
margin-right:12px!important;
}

.selection_nipple,
#sidebar_footer_nav.pinned_sidebar_footer {
display:none!important;
}

.ui_note.is_friend {
background:rgba(128,128,128,0.2)!important;
}

.post_avatar_link, .post_avatar {
background-color:transparent!important;
}

div#right_column a:before {
background:none!important;
border-right:none!important;
}

/*button changes*/
.chrome.blue,
.chrome.flat.close,
button.chrome,
.button.gray.light,
#cancel {
border-color:#d9d9d9!important;
background:#f0f0f0!important;
border-radius:0px !important;
color:grey!important;
}

.chrome.blue.txt,
.chrome.blue.options,
.chrome.grey,
.send_fan_mail,
#send {
background:#555!important;
color:#ddd!important;
}

a.follow:hover,
.control.follow.icon_plus:hover,
.note_follow {
background:grey!important;
border:grey!important;
}

span.follow_icon {
border-radius:3px!important;
}

form.dashboard_options_form {
border:none!important
}

/*remove blue reblog/edit etc overlay*/
.plexi.active,
.ui_peepr_glass,
.plexi.color.show,
.ui_dialog_lock.opaque,
.glass.show.blue {
background:rgba(0,0,0,0.25)!important;
}

body#dashboard_iframe,
#ask_form,
.search_landing_inner_full_wrapper:before,
.search_landing_inner {
background:none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/search"), url-prefix("http://www.tumblr.com/search") {
/*remove some blue bits*/
span.tumblelog_mask_item_fader,
span.tumblelog_mask_item.left,
.blogs_wrapper_carousel:after,
.blogs_wrapper_carousel:before,
.tumblelog_mask_item.right,
.l-content,
.l-container:before {
background:none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/mega-editor"), url-prefix("http://www.tumblr.com/mega-editor") {
html {
background:none!important;
}
 
.blue_bar {
border:none!important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/blog") {
#chart_activity span,
h1,
.ui_jumbo_avatar,
.active,
.ui_note a,
.ui_note span {
color:transparent!important;
}

#left_column{
background:none!important;
}
    
.date_header,
.part_full_date,
.part_relative_date {
color:white!important;
text-shadow:#000 0px 0px 2px!important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/tagged"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/new") {
#left_column{
background:none!important;
}
}
