css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.brokenbot.org") {
#container
{
	-moz-box-shadow:0 0 16px 4px rgba(255,233,229,1);
	-webkit-box-shadow:0 0 16px 4px rgba(255,233,229,1);
	border:1px #ffbeb2 dotted;
	border-radius:8px;
	box-shadow:0 0 16px 4px rgba(255,233,229,1);
}

cite,#help,#search,#p_stats,#forums,.thead,.codeblock .title
{
	background:#FF2700!important;
}

.menu a:active
{
	-moz-box-shadow:0 0 8px 0 rgba(255,147,128,1);
	-webkit-box-shadow:0 0 8px 0 rgba(255,147,128,1);
	background:#FF2700;
	border:1px #ffbeb2 dotted;
	border-radius:8px;
	box-shadow:0 0 8px 0 rgba(255,147,128,1);
	color:#fff;
	display:block;
	font-family:Tahoma,Verdana,Segoe,sans-serif;
	font-size:15px;
	font-style:normal;
	padding:8px 10px 10px;
	text-decoration:none;
}

.tfoot,.bfh
{
	background:#FFF;
	color:#FF2700;
}

.bfh
{
	-moz-box-shadow:0 0 16px 4px rgba(255,112,87,1);
	-webkit-box-shadow:0 0 16px 4px rgba(255,112,87,1);
	border:1px #ff7057 dotted;
	box-shadow:0 0 16px 4px rgba(255,112,87,1);
}

.onclick-menu-content
{
	background:#FF2700;
	border:1px #ffbeb2 dotted;
}

#portal,#portal a,#nav-portal a,#forums #nav-forums a,#p_stats #nav-stats a,#g_stats #nav-stats a,#search #nav-search a,#member #nav-member a,#calendar #nav-calendar a,#help #nav-help a,.menu a:active
{
	-moz-box-shadow:0 0 8px 0 rgba(255,147,128,1);
	-webkit-box-shadow:0 0 8px 0 rgba(255,147,128,1);
	background:#FF2700;
	border:1px #ffbeb2 dotted;
	border-radius:8px;
	box-shadow:0 0 8px 0 rgba(255,147,128,1);
	color:#fff;
	display:block;
	font-family:Tahoma,Verdana,Segoe,sans-serif;
	font-size:15px;
	font-style:normal;
	padding:4px 6px 6px;
	text-decoration:none;
}

.navMenu,.thead
{
	border-radius:6px;
}

.topbar
{
	-moz-box-shadow:0 0 16px 4px rgba(255,112,87,1);
	-webkit-box-shadow:0 0 16px 4px rgba(255,112,87,1);
	border:1px #ffbeb2 dotted;
	box-shadow:0 0 16px 4px rgba(255,112,87,1);
}
}
