css:
@-moz-document domain(lawnsite.com){
  
  /* CONTAINER FORMATTING */
  html, body{background-image: none !important; width: 100% !important; margin-left: 1px;}
  html>table, html>div, body>table, body>div, body>center, .grassDiv, .page div table, .navDiv, .innerNavDiv, .topnav, .subnav, .headerAdDiv, #darkBackgroundLayer{width: 100% !important; max-width: none !important; min-width: 0 !important;}
  #posts, .page, div.page, .pagenav, .header, .mainDiv, .mainDiv>div, .contentDiv, .outerContentDiv, .breadcrumbsDiv, .tborder, td.alt1, td.alt2, td.smallfont, .vbmenu_control, #collapseobj_forumrules, .notifications_menu{width: auto !important; max-width: none !important; min-width: 0 !important;}
  html, html>table, html>div, body, body>table, body>div, .pagenav, .breadcrumbsDiv, .outerContentDiv, .contentDiv, .outerContentDivdiv, .page, td.header, .leftmenu, .tfoot{color: #000 !important; background: #76829a !important;}
  body td[rowspan]:first-child img, .logo, .tcat_LHCBAR img[align], .headerAdsDiv, .headerAdDiv, .welcomeNewestMemberDiv, .logoDiv, .leftmenu, td.leftmenu img, div.leftmenuinner img, td.add, #homepagead, #searchad, .topAdsTable, .magazineArticlesHolderDiv, .magazineLeadinHolderOuterDiv, .TFmagazineLeadinHolderOuterDiv, .TFmagazineLeadinDiv, .videosDiv, #collapseobj_forumrules, .corporatefooter, .leftColumnDiv, .leftColumnDiv, #weatherHolder, .grassDiv, .grassCover, .grassDivInner, #AutoNumber1{visibility: hidden !important; width: 0 !important; height: 0 !important;}
  .normal, .last_visitors_list{background: inherit !important; color: #000 !important;}
  td.toplogintopleft, td.toplogintopright, td.toploginleft, td.toploginright, td.toplogincenter{background: #7ec0c6 !important; color: inherit;}
  .menuheader, #collapseobj_attachkey td.smallfont{background: #abcfe6 !important; color: inherit;} 
  .topnav, ul.topnav, .navDiv, .innerNavDiv, .mainDiv{margin: auto !important; float: none !important;}
  .navDiv{position: absolute !important; top: 0 !important; left: 5px !important; width: 60% !important;}
  #usercptools_menu, #navbar_search_menu, #notifications_menu, #pagenav_menu{display: none !important; visibility: visible !important;}
  td[id^="td_threadtitle_"].alt1 {width: 30%;}
  ul.topnav li a, ul.topnav li span {padding: 5px !important; margin: auto !important; margin-left: 0;}  
  .logindiv table{margin: 0 !important; width: auto !important;}
  .logindiv table td{background: #abcfe6 !important; color: inherit;}
  .logindiv{float:none !important; position:absolute; top:0; right:5px; z-index:100 !important; width: auto !important; background: #abcfe6 !important; border: inset 2px #661ba6 !important;}
  .slogan{position:absolute; top:0; left:5px; z-index:0 !important;}
  .slogan:before{content: "LawnSite.com .... ";}  
  td.vbmenu_control, td.vbmenu_option, td.vbmenu_popup, .tborder, .tborder .thead, td.thead, .thead .normal, .vbmenu_popup .thead, #link_bar .thead, .vbmenu_option, .block_title, #visitor_message_counters, td.tcat, .tcat_LHCBAR, .inlinemod{background: #6478a0 !important; color: inherit;}
  .tborder .tcat, .tcat_LHCBAR, #visitor_message_counters{border-top: 1px #000 inset !important; border-bottom: 1px #661ba6 inset !important;}
  .tborder>tbody .tcat[width] {border:0 !important;}
  td.panelsurround, #link_bar ul, #link_bar li, .vbmenu_popup{background: #93a1bc !important; color: inherit;}
  div.alt1, td.alt1, .alt1Active{background: #96a1b8 !important; color: inherit;}
  div.alt2, td.alt2, .alt2Active{background: #b7c6e1 !important; color: inherit;}
  pre.alt2, .panel fieldset table td[style]{background-color: #d4ddea !important; color: inherit;}
  .panel{background: #c5d0e7 !important; color: inherit;}
  textarea{background: #d2d9e0 !important; color: inherit;}
  .vBulletin_editor{background: #76829a !important; color: inherit;}
  #vB_Editor_001_smiliebox .inlineimg{width: 22px; height: 21px; padding: 1px;}
  .button{background: #7bccc2 !important; color: inherit;}
  
  /* NOTE: Following line limits posted images width to 600px for consistent layout */

  .alt1 img{max-width:600px;}

  /* FONT & LINK FORMATTING */

  html, body{font-size: 100.01% !important;}
  div, table, tr, td, ul, dt, li, .logo, .page, .tcat, .shade, .tborder, .tcat, alt1{color:#000 !important; background: inherit; font-size: 1.005em !important;}
  .smallfont, .smallfontlogin, .smallfont div, .smallfontlogin div, .time, .alt2, .fieldset legend{color:#000 !important; background: inherit; font-size: 0.85em !important;}
  .shade{font-weight:bold !important;}
  strong{font-size: inherit !important;}
  h4, #profile_tabs{color:#000 !important; background: inherit; font-size: 1.02em !important;}

  a:link, html>a:link, .menu a:link, td a:link, .tcat_alink{color: #2b4d93 !important; background: inherit; text-decoration:none !important;}
  a:visited, html>a:visited, td a:visited, .tcat_avisited, .menu a:visited{color: #2b4d93 !important; background: inherit; text-decoration:none !important;}
  a:hover, html>a:hover, td a:hover, .tcat_ahover, .menu a:hover, div.normal a:hover, td.vbmenu_control a:hover{color: #58ed66 !important; background: inherit; text-decoration:underline !important;}
  a:active, html>a:active, .tcat a:active, .menu a:active{color: #661ba6 !important; background: inherit;}
  .menucell a:hover, .vbmenu_popup a:hover, td.vbmenu_option_alink a:hover, .floatcontainer a:hover, .vbmenu_hilite{background:inherit !important; color: inherit;}

}
