css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("badoo.com") {

    body, #head {background-color: #585858 !important; }
    
    .sidebar-logo .logo {
    visibility: hidden;
    opacity: 1;
    transition: opacity 0.3s ease-in-out 0.13s, visibility 0.3s ease-in-out 0.13s;}
    
    .table .table-cell--bottom { visibility: hidden;;}
    
    .section-header { background-color: #585858 !important;}
    
    .section-header .banner-container {visibility: hidden;}

    .section-content  {background-color: #585858 !important; }
    
    .user-card__info-name  {color: #F3E2A9 !important; }
       
    .btn-game--not > .btn-game__ico { background-position: -48px -529px;}
    
    .section-left-side  {background-color: #dbdbdb !important; }
  
    .btn-game {
    display: inline-block;
    position: relative;
    width: 50px;
    height: 50px;
    padding: 0px 0px;
    cursor: pointer;
    border: 2px solid transparent;
    border-radius: 100%;
    background: none repeat scroll 0% 0% rgba(173, 0, 0, 0.6);}
    
    .sidebar .table  {background-color: #dbdbdb !important; }

    .section-chat-cnt .section-messages--with-banner .scroll  {background-color: #fafafa !important; }
    
    .sidebar-nav .sidebar-nav__btn {display: none !important}
    
   .profile-header__name  {color: #F5A9BC !important; }
    
    .banner-container {display: none !important;
    color: #585858 !important; }
    
    .page-mm .with-side-footer .page-right-side, .page-profile .with-side-footer .page-right-side  {background-color: #585858 !important; }
    
    .page-right-footer  {background-color: #dbdbdb !important; }
    
    #first {background-color: #dbdbdb !important; }
    }
