css:
@-moz-document domain("tree.taiga.io") {
html,
body {
    background: #000 url(https://images.blogthings.com/thecolorfulpatterntest/pattern-4.png) repeat;
}
nav.navbar {
    background: rgba(0, 0, 0, 0.8);
}
h1,
h2,
h3,
h4,
h5 {
    font-family: opensans-regular;
}
h1 span.date {
    float: right;
    text-transform: none;
    font-size: 2rem;
}
.trans-button {
    font-family: opensans-regular;
}
section.main.taskboard {
    padding: 0;
}
section.main.taskboard h1 {
    margin: 0;
}
section.main.taskboard h1 .project-name-short {
    margin-left: 15px;
}
section.main.taskboard .summary {
    margin-bottom: 0;
}
section.main.taskboard .taskboard-table-header {
    background: #666;
    margin-bottom: 0;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
}
section.main.taskboard .taskboard-table-header .taskboard-table-inner h2 {
    background: #666;
    color: #fff;
}
section.backlog {
    padding: 10px 20px;
}
.sprints .sprint-table .us-name {
    max-width: 730px;
    width: 730px;
}
.taskboard-table-header .task-colum-name {
    font-family: opensans-regular;
    font-size: 1rem;
}
.sprints .sprint .sprint-date,
.sprints .sprint-name a,
.taskboard-userstory-box .us-title {
    font-family: opensans-regular;
    font-size: 14px;
}
.taskboard-task {
    margin-bottom: 10px;
}
.taskboard-task-inner {
    margin-bottom: 5px;
    padding: 5px;
}
.taskboard-task .taskboard-tagline {
    height: 5px;
}
.taskboard-task .task-name {
    font-family: opensans-regular;
    font-size: 12px;
}
.taskboard-table-body .taskboard-userstory-box .icon.icon-vfold {
    left: 3px;
}
div.main section.duty-content strong {
    font-family: opensans-semibold;
}
.backlog-table-body.show-tags .tags-block,
.backlog-table-header.show-tags .tags-block {
    float: right;
}
.tags-block .tag {
    margin: 0 .5rem 0 0;
}
.wysiwyg code,
.wysiwyg pre {
    background: #2e2e2e;
}
.codehilite .c1,
.codehilite .cm,
.codehilite .cp,
.codehilite .cs,
.codehilite .c {
    color: #A5A297;
}
}
