@-moz-document domain("geocheck.org"), domain("geotjek.dk") {
tr.header td { background-color: #35598e !important; }
td.pagerh { color: black !important; }
td, td.RightSidBar { background: white !important; }
th, td.sidebar_dark { background: whitesmoke !important; color: black !important; } 
div.cachedata { display: inline; }
.geomain { border-top: 0 !important; margin: 0 auto !important; } 
td.footer { border-top: solid 1px black !important; }
td.footer, td.footer a { color: black !important; }
img[src="pics/layout/GeoCheck-Layout_01.png"] { display: none !important; }
:not(.center) > a[href="/index.php"]:nth-of-type(1)::before
{
    font-size: xx-large;
    font-weight: bolder;
    float: left;
    content: "GeoCheck";
    padding: 0 10px !important;
}
:not(.center) > a[href="/index.php"	]:nth-of-type(1)::after
{
    font-style: italic;
    float: left;
    content: "Check your cache coordinates online";
    padding: 0 10px !important;
}

/* replace icons with more modern ones */
.menu a 
{
    text-decoration: none;
	position: relative;
    top: 6px;
}

.menu a:before
{ 
    content: " ";
    display: inline-block;
    width: 32px;
    height: 32px;
    background-repeat: no-repeat;
}

.menu a:after 
{
    font-size: large;
    padding-left: 8px;
    vertical-align: super; 
}

.menu a:hover { text-decoration: underline; }

img[src="pics/icons/menu/menuHome.gif"],
img[src="pics/icons/menu/menuContact.gif"],
img[src="pics/icons/menu/menuNewAccount.gif"],
img[src="pics/icons/menu/menuLogin.gif"],
img[src="pics/icons/menu/menuMyAccount.gif"],
img[src="pics/icons/menu/menuNewSolution.gif"],
img[src="pics/icons/menu/menuChangePW.gif"],
img[src="pics/icons/menu/menuLogout.gif"]
{
    display: none !important; 
}

